00102         private void AddSpriteAssetInternal(int hashCode, TMP_SpriteAsset spriteAsset)
00103         {
00104             if (m_SpriteAssetReferenceLookup.ContainsKey(hashCode)) return;
00105 
00106             // Add reference to Sprite Asset.
00107             m_SpriteAssetReferenceLookup.Add(hashCode, spriteAsset);
00108 
00109             // Add reference to Sprite Asset using the asset hashcode.
00110             m_FontMaterialReferenceLookup.Add(hashCode, spriteAsset.material);
00111 
00112             // Compatibility check
00113             if (spriteAsset.hashCode == 0) spriteAsset.hashCode = hashCode;
00114         }
