00517     public struct MaterialReference
00518     {
00519 
00520         public int index;
00521         public TMP_FontAsset fontAsset;
00522         public TMP_SpriteAsset spriteAsset;
00523         public Material material;
00524         public bool isDefaultMaterial;
00525         public bool isFallbackMaterial;
00526         public Material fallbackMaterial;
00527         public float padding;
00528         public int referenceCount;
00529 
00530 
00539         public MaterialReference(int index, TMP_FontAsset fontAsset, TMP_SpriteAsset spriteAsset, Material material, float padding)
00540         {
00541             this.index = index;
00542             this.fontAsset = fontAsset;
00543             this.spriteAsset = spriteAsset;
00544             this.material = material;
00545             this.isDefaultMaterial = material.GetInstanceID() == fontAsset.material.GetInstanceID() ? true : false;
00546             this.isFallbackMaterial = false;
00547             this.fallbackMaterial = null;
00548             this.padding = padding;
00549             this.referenceCount = 0;
00550         }
00551 
00552 
00559         public static bool Contains(MaterialReference[] materialReferences, TMP_FontAsset fontAsset)
00560         {
00561             int id = fontAsset.GetInstanceID();
00562 
00563             for (int i = 0; i < materialReferences.Length && materialReferences[i].fontAsset != null; i++)
00564             {
00565                 if (materialReferences[i].fontAsset.GetInstanceID() == id)
00566                     return true;
00567             }
00568 
00569             return false;
00570         }
00571 
00572 
00581         public static int AddMaterialReference(Material material, TMP_FontAsset fontAsset, MaterialReference[] materialReferences, Dictionary<int, int> materialReferenceIndexLookup)
00582         {
00583             int materialID = material.GetInstanceID();
00584             int index = 0;
00585 
00586             if (materialReferenceIndexLookup.TryGetValue(materialID, out index))
00587             {
00588                 return index;
00589             }
00590             else
00591             {
00592                 index = materialReferenceIndexLookup.Count;
00593 
00594                 // Add new reference index
00595                 materialReferenceIndexLookup[materialID] = index;
00596 
00597                 materialReferences[index].index = index;
00598                 materialReferences[index].fontAsset = fontAsset;
00599                 materialReferences[index].spriteAsset = null;
00600                 materialReferences[index].material = material;
00601                 materialReferences[index].isDefaultMaterial = materialID == fontAsset.material.GetInstanceID() ? true : false;
00602                 //materialReferences[index].padding = 0;
00603                 materialReferences[index].referenceCount = 0;
00604 
00605                 return index;
00606             }
00607         }
00608 
00609 
00618         public static int AddMaterialReference(Material material, TMP_SpriteAsset spriteAsset, MaterialReference[] materialReferences, Dictionary<int, int> materialReferenceIndexLookup)
00619         {
00620             int materialID = material.GetInstanceID();
00621             int index = 0;
00622 
00623             if (materialReferenceIndexLookup.TryGetValue(materialID, out index))
00624             {
00625                 return index;
00626             }
00627             else
00628             {
00629                 index = materialReferenceIndexLookup.Count;
00630 
00631                 // Add new reference index
00632                 materialReferenceIndexLookup[materialID] = index;
00633 
00634                 materialReferences[index].index = index;
00635                 materialReferences[index].fontAsset = materialReferences[0].fontAsset;
00636                 materialReferences[index].spriteAsset = spriteAsset;
00637                 materialReferences[index].material = material;
00638                 materialReferences[index].isDefaultMaterial = true;
00639                 //materialReferences[index].padding = 0;
00640                 materialReferences[index].referenceCount = 0;
00641 
00642                 return index;
00643             }
00644         }
