00010 using InteractionEngineUtility;
00011 using Leap.Unity.Query;
00012 using System.Collections;
00013 using System.Collections.Generic;
00014 using UnityEngine;
00015 
00016 namespace Leap.Unity.Interaction {
00017 
00024   public class NonKinematicGraspedMovement : IGraspedMovementHandler {
00025 
00026     protected float _maxVelocity = 6F;
00027 
00028     private Vector3 _lastSolvedCoMPosition = Vector3.zero;
00029     protected AnimationCurve _strengthByDistance = new AnimationCurve(new Keyframe(0.0f, 1.0f, 0.0f, 0.0f),
00030                                                                       new Keyframe(0.02f, 0.3f, 0.0f, 0.0f));
00031 
00032     public void MoveTo(Vector3 solvedPosition, Quaternion solvedRotation,
00033                        InteractionBehaviour intObj, bool justGrasped) {
00034       Vector3 solvedCenterOfMass = solvedRotation * intObj.rigidbody.centerOfMass + solvedPosition;
00035       Vector3 currCenterOfMass = intObj.rigidbody.rotation * intObj.rigidbody.centerOfMass + intObj.rigidbody.position;
00036 
00037       Vector3 targetVelocity = PhysicsUtility.ToLinearVelocity(currCenterOfMass, solvedCenterOfMass, Time.fixedDeltaTime);
00038 
00039       Vector3 targetAngularVelocity = PhysicsUtility.ToAngularVelocity(intObj.rigidbody.rotation, solvedRotation, Time.fixedDeltaTime);
00040 
00041       // Clamp targetVelocity by _maxVelocity.
00042       float maxScaledVelocity = _maxVelocity * intObj.manager.SimulationScale;
00043       float targetSpeedSqrd = targetVelocity.sqrMagnitude;
00044       if (targetSpeedSqrd > maxScaledVelocity * maxScaledVelocity) {
00045         float targetPercent = maxScaledVelocity / Mathf.Sqrt(targetSpeedSqrd);
00046         targetVelocity *= targetPercent;
00047         targetAngularVelocity *= targetPercent;
00048       }
00049 
00050       float followStrength = 1F;
00051       if (!justGrasped) {
00052         float remainingDistanceLastFrame = Vector3.Distance(_lastSolvedCoMPosition, currCenterOfMass);
00053         followStrength = _strengthByDistance.Evaluate(remainingDistanceLastFrame / intObj.manager.SimulationScale);
00054       }
00055 
00056       Vector3 lerpedVelocity = Vector3.Lerp(intObj.rigidbody.velocity, targetVelocity, followStrength);
00057       Vector3 lerpedAngularVelocity = Vector3.Lerp(intObj.rigidbody.angularVelocity, targetAngularVelocity, followStrength);
00058 
00059       intObj.rigidbody.velocity = lerpedVelocity;
00060       intObj.rigidbody.angularVelocity = lerpedAngularVelocity;
00061 
00062       _lastSolvedCoMPosition = solvedCenterOfMass;
00063 
00064       // Store the target position and rotation to prevent slippage in SwapGrasp
00065       // scenarios.
00066       intObj.latestScheduledGraspPose = new Pose(solvedPosition, solvedRotation);
00067     }
00068   }
00069 
00070 }
