00016     internal class PackageAddFromUrlField : VisualElement
00017     {
00018 #if UNITY_2018_3_OR_NEWER
00019         internal new class UxmlFactory : UxmlFactory<PackageAddFromUrlField> { }
00020 #endif
00021         private string urlText;
00022 
00023         private readonly VisualElement root;
00024 
00025         public PackageAddFromUrlField()
00026         {
00027             root = Resources.GetTemplate("PackageAddFromUrlField.uxml");
00028             Add(root);
00029 
00030             UrlTextField.value = urlText;
00031 
00032             AddButton.SetEnabled(!string.IsNullOrEmpty(urlText));
00033             AddButton.clickable.clicked += OnAddButtonClick;
00034 
00035             RegisterCallback<AttachToPanelEvent>(OnEnterPanel);
00036             RegisterCallback<DetachFromPanelEvent>(OnLeavePanel);
00037         }
00038 
00039         private void OnUrlTextFieldChange(ChangeEvent<string> evt)
00040         {
00041             urlText = evt.newValue;
00042             AddButton.SetEnabled(!string.IsNullOrEmpty(urlText));
00043         }
00044 
00045         private void OnUrlTextFieldFocus(FocusEvent evt)
00046         {
00047             Show();
00048         }
00049 
00050         private void OnUrlTextFieldFocusOut(FocusOutEvent evt)
00051         {
00052             Hide();
00053         }
00054 
00055         private void OnContainerFocus(FocusEvent evt)
00056         {
00057             UrlTextField.Focus();
00058         }
00059 
00060         private void OnContainerFocusOut(FocusOutEvent evt)
00061         {
00062             Hide();
00063         }
00064 
00065         private void OnEnterPanel(AttachToPanelEvent evt)
00066         {
00067             AddFromUrlFieldContainer.RegisterCallback<FocusEvent>(OnContainerFocus);
00068             AddFromUrlFieldContainer.RegisterCallback<FocusOutEvent>(OnContainerFocusOut);
00069             UrlTextField.RegisterCallback<FocusEvent>(OnUrlTextFieldFocus);
00070             UrlTextField.RegisterCallback<FocusOutEvent>(OnUrlTextFieldFocusOut);
00071             UrlTextField.RegisterCallback<ChangeEvent<string>>(OnUrlTextFieldChange);
00072             UrlTextField.RegisterCallback<KeyDownEvent>(OnKeyDownShortcut);
00073             Hide();
00074         }
00075 
00076         private void OnLeavePanel(DetachFromPanelEvent evt)
00077         {
00078             AddFromUrlFieldContainer.UnregisterCallback<FocusEvent>(OnContainerFocus);
00079             AddFromUrlFieldContainer.UnregisterCallback<FocusOutEvent>(OnContainerFocusOut);
00080             UrlTextField.UnregisterCallback<FocusEvent>(OnUrlTextFieldFocus);
00081             UrlTextField.UnregisterCallback<FocusOutEvent>(OnUrlTextFieldFocusOut);
00082             UrlTextField.UnregisterCallback<ChangeEvent<string>>(OnUrlTextFieldChange);
00083             UrlTextField.UnregisterCallback<KeyDownEvent>(OnKeyDownShortcut);
00084         }
00085 
00086         private void OnKeyDownShortcut(KeyDownEvent evt)
00087         {
00088             switch (evt.keyCode)
00089             {
00090                 case KeyCode.Escape:
00091                     Hide();
00092                     break;
00093                 case KeyCode.Return:
00094                 case KeyCode.KeypadEnter:
00095                     OnAddButtonClick();
00096                     break;
00097             }
00098         }
00099 
00100         private void OnAddButtonClick()
00101         {
00102             var path = urlText;
00103             if (!string.IsNullOrEmpty(path) && !Package.AddRemoveOperationInProgress)
00104             {
00105                 Package.AddFromLocalDisk(path);
00106                 Hide();
00107             }
00108         }
00109 
00110         internal void Hide()
00111         {
00112             UIUtils.SetElementDisplay(this, false);
00113         }
00114 
00115         internal void Show(bool reset = false)
00116         {
00117             if (reset)
00118                 Reset();
00119             UIUtils.SetElementDisplay(this, true);
00120         }
00121 
00122         private void Reset()
00123         {
00124             UrlTextField.value = string.Empty;
00125             urlText = string.Empty;
00126             AddButton.SetEnabled(false);
00127             UrlTextField.Focus();
00128         }
00129 
00130         private VisualElement AddFromUrlFieldContainer { get { return root.Q<VisualElement>("addFromUrlFieldContainer");  }}
00131         private TextField UrlTextField { get { return root.Q<TextField>("urlTextField"); } }
00132         private Button AddButton{ get { return root.Q<Button>("addFromUrlButton");  }}
00133     }
