00116         internal void SetDisplayPackage(PackageInfo packageInfo)
00117         {
00118             DisplayPackage = packageInfo;
00119             
00120             var detailVisible = true;
00121             Error error = null;
00122 
00123             if (package == null || DisplayPackage == null)
00124             {
00125                 detailVisible = false;
00126                 UIUtils.SetElementDisplay(DocumentationContainer, false);
00127                 UIUtils.SetElementDisplay(CustomContainer, false);
00128                 UIUtils.SetElementDisplay(UpdateBuiltIn, false);
00129 
00130                 foreach (var extension in PackageManagerExtensions.Extensions)
00131                     extension.OnPackageSelectionChange(null);
00132             }
00133             else
00134             {
00135                 SetUpdateVisibility(true);
00136                 UIUtils.SetElementDisplay(ViewDocButton, true);
00137                 RemoveButton.visible = true;
00138 
00139                 if (string.IsNullOrEmpty(DisplayPackage.Description))
00140                 {
00141                     DetailDesc.text = "There is no description for this package.";
00142                     DetailDesc.AddToClassList(emptyDescriptionClass);
00143                 }
00144                 else
00145                 {
00146                     DetailDesc.text = DisplayPackage.Description;
00147                     DetailDesc.RemoveFromClassList(emptyDescriptionClass);
00148                 }
00149 
00150                 root.Q<Label>("detailTitle").text = DisplayPackage.DisplayName;
00151                 DetailVersion.text = "Version " + DisplayPackage.VersionWithoutTag;
00152 
00153                 if (DisplayPackage.IsInDevelopment || DisplayPackage.HasVersionTag(PackageTag.preview))
00154                     UIUtils.SetElementDisplay(GetTag(PackageTag.verified), false);
00155                 else
00156                 {
00157                     var unityVersionParts = Application.unityVersion.Split('.');
00158                     var unityVersion = string.Format("{0}.{1}", unityVersionParts[0], unityVersionParts[1]);
00159                     VerifyLabel.text = unityVersion + " verified";
00160                     UIUtils.SetElementDisplay(GetTag(PackageTag.verified), DisplayPackage.IsVerified);
00161                 }
00162 
00163                 UIUtils.SetElementDisplay(GetTag(PackageTag.inDevelopment), DisplayPackage.IsInDevelopment);
00164                 UIUtils.SetElementDisplay(GetTag(PackageTag.local), DisplayPackage.IsLocal);
00165                 UIUtils.SetElementDisplay(GetTag(PackageTag.preview), DisplayPackage.IsPreview);
00166 
00167                 UIUtils.SetElementDisplay(DocumentationContainer, DisplayPackage.Origin != PackageSource.BuiltIn);
00168                 UIUtils.SetElementDisplay(ChangelogContainer, DisplayPackage.HasChangelog(DisplayPackage));
00169 
00170                 root.Q<Label>("detailName").text = DisplayPackage.Name;
00171                 root.Q<ScrollView>("detailView").scrollOffset = new Vector2(0, 0);
00172 
00173                 DetailModuleReference.text = "";
00174                 var isBuiltIn = DisplayPackage.IsBuiltIn;
00175                 if (isBuiltIn)
00176                     DetailModuleReference.text = DisplayPackage.BuiltInDescription;
00177 
00178                 DetailAuthor.text = "";
00179                 if (!string.IsNullOrEmpty(DisplayPackage.Author))
00180                     DetailAuthor.text = string.Format("Author: {0}", DisplayPackage.Author);
00181 
00182                 UIUtils.SetElementDisplay(DetailDesc, !isBuiltIn);
00183                 UIUtils.SetElementDisplay(DetailVersion, !isBuiltIn);
00184                 UIUtils.SetElementDisplayNonEmpty(DetailModuleReference);
00185                 UIUtils.SetElementDisplayNonEmpty(DetailAuthor);
00186 
00187                 if (DisplayPackage.Errors.Count > 0)
00188                     error = DisplayPackage.Errors.First();
00189 
00190                 RefreshAddButton();
00191                 RefreshRemoveButton();
00192                 UIUtils.SetElementDisplay(CustomContainer, true);
00193 
00194                 package.AddSignal.OnOperation += OnAddOperation;
00195                 package.RemoveSignal.OnOperation += OnRemoveOperation;
00196                 foreach (var extension in PackageManagerExtensions.Extensions)
00197                     extension.OnPackageSelectionChange(DisplayPackage.Info);
00198             }
00199 
00200             // Set visibility
00201             root.Q<VisualElement>("detail").visible = detailVisible;
00202 
00203             if (null == error)
00204                 error = PackageCollection.Instance.GetPackageError(package);
00205 
00206             if (error != null)
00207                 SetError(error);
00208             else
00209                 DetailError.ClearError();            
00210         }
