00212         private void ResetVersionItems(PackageInfo displayPackage)
00213         {
00214             VersionItems.Clear();            
00215             VersionPopup.SetEnabled(true);
00216 
00217             if (displayPackage == null)
00218                 return;
00219             
00220             //
00221             // Get key versions -- Latest, Verified, LatestPatch, Current.
00222             var keyVersions = new List<PackageInfo>();
00223             if (package.LatestRelease != null) keyVersions.Add(package.LatestRelease);
00224             if (package.Current != null) keyVersions.Add(package.Current);
00225             if (package.Verified != null && package.Verified != package.Current) keyVersions.Add(package.Verified);
00226             if (package.LatestPatch != null && package.IsAfterCurrentVersion(package.LatestPatch)) keyVersions.Add(package.LatestPatch);
00227             if (package.Current == null && package.LatestRelease == null && package.Latest != null) keyVersions.Add(package.Latest);
00228             if (Package.ShouldProposeLatestVersions && package.Latest != package.LatestRelease && package.Latest != null) keyVersions.Add(package.Latest);
00229             keyVersions.Add(package.LatestUpdate);        // Make sure LatestUpdate is always in the list.
00230 
00231             foreach (var version in keyVersions.OrderBy(package => package.Version).Reverse())
00232             {
00233                 var item = new VersionItem {Version = version};
00234                 VersionItems.Add(item);
00235                 
00236                 if (version == package.LatestUpdate)
00237                     VersionPopup.value = item;
00238             }
00239 
00240             //
00241             // Add all versions
00242             foreach (var version in package.Versions.Reverse())
00243             {
00244                 var item = new VersionItem {Version = version};
00245                 item.MenuName = "All Versions/";
00246                 VersionItems.Add(item);
00247             }
00248             
00249             if (VersionItems.Count == 0)
00250             {
00251                 VersionItems.Add(EmptyVersion);
00252                 VersionPopup.value = EmptyVersion;
00253                 VersionPopup.SetEnabled(false);
00254             }
00255         }
