00257         public void SetPackage(Package package)
00258         {
00259             if (this.package != null)
00260             {
00261                 if (this.package.AddSignal.Operation != null)
00262                 {
00263                     this.package.AddSignal.Operation.OnOperationError -= OnAddOperationError;
00264                     this.package.AddSignal.Operation.OnOperationSuccess -= OnAddOperationSuccess;
00265                 }
00266                 this.package.AddSignal.ResetEvents();
00267 
00268                 if (this.package.RemoveSignal.Operation != null)
00269                 {
00270                     this.package.RemoveSignal.Operation.OnOperationSuccess -= OnRemoveOperationSuccess;
00271                     this.package.RemoveSignal.Operation.OnOperationError -= OnRemoveOperationError;
00272                 }
00273                 this.package.RemoveSignal.ResetEvents();
00274             }
00275 
00276             UIUtils.SetElementDisplay(this, true);
00277 
00278             this.package = package;
00279             var displayPackage = package != null ? package.VersionToDisplay : null;
00280             ResetVersionItems(displayPackage);
00281             SetDisplayPackage(displayPackage);
00282         }
