00301         private void OnAddOperationError(Error error)
00302         {
00303             if (package != null && package.AddSignal.Operation != null)
00304             {
00305                 package.AddSignal.Operation.OnOperationSuccess -= OnAddOperationSuccess;
00306                 package.AddSignal.Operation.OnOperationError -= OnAddOperationError;
00307                 package.AddSignal.Operation = null;
00308             }
00309 
00310             PackageCollection.Instance.AddPackageError(package, error);
00311 
00312             SetError(error);
00313             if (package != null)
00314                 ResetVersionItems(package.VersionToDisplay);
00315             PackageCollection.Instance.UpdatePackageCollection();
00316         }
00317 
00318         private void OnAddOperationSuccess(PackageInfo packageInfo)
00319         {
00320             if (package != null && package.AddSignal.Operation != null)
00321             {
00322                 package.AddSignal.Operation.OnOperationSuccess -= OnAddOperationSuccess;
00323                 package.AddSignal.Operation.OnOperationError -= OnAddOperationError;
00324                 package.AddSignal.Operation = null;
00325             }
00326 
00327             foreach (var extension in PackageManagerExtensions.Extensions)
00328                 extension.OnPackageAddedOrUpdated(packageInfo.Info);
00329         }
00330 
00331         private void OnRemoveOperation(IRemoveOperation operation)
