00369         private void RefreshAddButton()
00370         {
00371             if (package.Current != null && package.Current.IsInDevelopment)
00372             {
00373                 UIUtils.SetElementDisplay(UpdateBuiltIn, false);
00374                 UIUtils.SetElementDisplay(UpdateCombo, false);
00375                 UIUtils.SetElementDisplay(UpdateButton, false);
00376                 return;
00377             }
00378 
00379             var targetVersion = SelectedPackage;
00380             if (targetVersion == null)
00381                 return;
00382             
00383             var enableButton = !Package.AddRemoveOperationInProgress;
00384             var enableVersionButton = true;
00385             
00386             var action = PackageAction.Update;
00387             var inprogress = false;
00388             var isBuiltIn = package.IsBuiltIn;
00389             SemVersion version = null;
00390             
00391             if (package.AddSignal.Operation != null)
00392             {
00393                 if (isBuiltIn)
00394                 {
00395                     action = PackageAction.Enable;
00396                     inprogress = true;
00397                     enableButton = false;                    
00398                 }
00399                 else
00400                 {
00401                     var addOperationVersion = package.AddSignal.Operation.PackageInfo.Version;
00402                     if (package.Current == null)
00403                     {
00404                         action = PackageAction.Add;
00405                         inprogress = true;
00406                     }
00407                     else
00408                     {
00409                         action = addOperationVersion.CompareByPrecedence(package.Current.Version) >= 0
00410                             ? PackageAction.Update : PackageAction.Downgrade;
00411                         inprogress = true;
00412                     }
00413                 
00414                     enableButton = false;
00415                     enableVersionButton = false;
00416                 }
00417             } 
00418             else 
00419             {
00420                 if (package.Current != null)
00421                 {
00422                     // Installed
00423                     if (package.Current.IsVersionLocked)
00424                     {
00425                         if (package.Current.Origin == PackageSource.Embedded)
00426                             action = PackageAction.Embedded;
00427                         else if (package.Current.Origin == PackageSource.Git)
00428                             action = PackageAction.Git;
00429                         
00430                         enableButton = false;
00431                         enableVersionButton = false;
00432                     }
00433                     else
00434                     {
00435                         if (targetVersion.IsCurrent)
00436                         {
00437                             if (targetVersion == package.LatestUpdate)
00438                                 action = PackageAction.UpToDate;
00439                             else
00440                                 action = PackageAction.Current;
00441                             
00442                             enableButton = false;
00443                         }
00444                         else
00445                         {
00446                             action = targetVersion.Version.CompareByPrecedence(package.Current.Version) >= 0
00447                                 ? PackageAction.Update : PackageAction.Downgrade;
00448                         }
00449                     }
00450                 }
00451                 else
00452                 {
00453                     // Not Installed
00454                     if (package.Versions.Any())
00455                     {
00456                         if (isBuiltIn)
00457                             action = PackageAction.Enable;
00458                         else
00459                             action = PackageAction.Add;
00460                     }
00461                 }
00462             }
00463 
00464             if (package.RemoveSignal.Operation != null)
00465                 enableButton = false;
00466 
00467             if (EditorApplication.isCompiling)
00468             {
00469                 enableButton = false;
00470                 enableVersionButton = false;
00471 
00472                 EditorApplication.update -= CheckCompilationStatus;
00473                 EditorApplication.update += CheckCompilationStatus;
00474             }
00475             
00476             var button = isBuiltIn ? UpdateBuiltIn : UpdateButton;
00477             button.SetEnabled(enableButton);
00478             VersionPopup.SetEnabled(enableVersionButton);
00479             button.text = GetButtonText(action, inprogress, version);
00480 
00481             var visibleFlag = !(package.Current != null && package.Current.IsVersionLocked);
00482             UIUtils.SetElementDisplay(UpdateBuiltIn, isBuiltIn && visibleFlag);
00483             UIUtils.SetElementDisplay(UpdateCombo, !isBuiltIn && visibleFlag);
00484             UIUtils.SetElementDisplay(UpdateButton, !isBuiltIn && visibleFlag);
00485         }
00486 
00487         private void RefreshRemoveButton()
00488         {
00489             var visibleFlag = false;
00490 
00491             var current = package.Current;
00492             
00493             // Show only if there is a current package installed
00494             if (current != null)
00495             {
00496                 visibleFlag = current.CanBeRemoved && !package.IsPackageManagerUI;
00497 
00498                 var action = current.IsBuiltIn ? PackageAction.Disable : PackageAction.Remove;
00499                 var inprogress = package.RemoveSignal.Operation != null;
00500 
00501                 var enableButton = visibleFlag && !EditorApplication.isCompiling && !inprogress && !Package.AddRemoveOperationInProgress;
00502 
00503                 if (EditorApplication.isCompiling)
00504                 {
00505                     EditorApplication.update -= CheckCompilationStatus;
00506                     EditorApplication.update += CheckCompilationStatus;
00507                 }
00508 
00509                 RemoveButton.SetEnabled(enableButton);
00510                 RemoveButton.text = GetButtonText(action, inprogress);                   
00511             }
00512 
00513             UIUtils.SetElementDisplay(RemoveButton, visibleFlag);
00514         }
00515 
00516         private void CheckCompilationStatus()
00517         {
00518             if (EditorApplication.isCompiling)
00519                 return;
00520 
00521             RefreshAddButton();
00522             RefreshRemoveButton();
00523             EditorApplication.update -= CheckCompilationStatus;
00524         }
