00526         private static string GetButtonText(PackageAction action, bool inProgress = false, SemVersion version = null)
00527         {
00528             return version == null ?
00529                 string.Format("{0}", inProgress ? PackageActionInProgressVerbs[(int) action] : PackageActionVerbs[(int) action]) :
00530                 string.Format("{0} {1}", inProgress ? PackageActionInProgressVerbs[(int) action] : PackageActionVerbs[(int) action], version);
00531         }
