00533         private void UpdateClick()
00534         {
00535             if (package.IsPackageManagerUI)
00536             {
00537                 // Let's not allow updating of the UI if there are build errrors, as for now, that will prevent the UI from reloading properly.
00538                 if (EditorUtility.scriptCompilationFailed)
00539                 {
00540                     EditorUtility.DisplayDialog("Unity Package Manager", "The Package Manager UI cannot be updated while there are script compilation errors in your project.  Please fix the errors and try again.", "Ok");
00541                     return;
00542                 }
00543 
00544                 if (!EditorUtility.DisplayDialog("Unity Package Manager", "Updating this package will close the Package Manager window. You will have to re-open it after the update is done. Do you want to continue?", "Yes", "No"))
00545                     return;
00546 
00547                 if (package.AddSignal.Operation != null)
00548                 {
00549                     package.AddSignal.Operation.OnOperationSuccess -= OnAddOperationSuccess;
00550                     package.AddSignal.Operation.OnOperationError -= OnAddOperationError;
00551                     package.AddSignal.ResetEvents();
00552                     package.AddSignal.Operation = null;
00553                 }
00554 
00555                 DetailError.ClearError();
00556                 EditorApplication.update += CloseAndUpdate;
00557 
00558                 return;
00559             }
00560 
00561             DetailError.ClearError();
00562             package.Add(SelectedPackage);
00563             RefreshAddButton();
00564             RefreshRemoveButton();
00565         }
00566 
00567         private void CloseAndUpdate()
00568         {
00569             EditorApplication.update -= CloseAndUpdate;
00570             package.Add(SelectedPackage);
00571 
00572             var windows = UnityEngine.Resources.FindObjectsOfTypeAll<PackageManagerWindow>();
00573             if (windows.Length > 0)
00574             {
00575                 windows[0].Close();
00576             }
00577         }
