00084         private void UpdateGroups()
00085         {
00086             foreach (var group in Groups)
00087             {
00088                 PackageItem firstPackage = null;
00089                 PackageItem lastPackage = null;
00090 
00091                 var listGroup = group.Query<PackageItem>().ToList();
00092                 foreach (var item in listGroup)
00093                 {
00094                     if (!item.visible)
00095                         continue;
00096 
00097                     if (firstPackage == null) firstPackage = item;
00098                     lastPackage = item;
00099                 }
00100 
00101                 if (firstPackage == null && lastPackage == null)
00102                 {
00103                     UIUtils.SetElementDisplay(group, false);
00104                 }
00105                 else 
00106                 {
00107                     UIUtils.SetElementDisplay(group, true);
00108                     group.firstPackage = firstPackage;
00109                     group.lastPackage = lastPackage;
00110                 }
00111             }
00112         }
