00124         private void ScrollIfNeeded()
00125         {
00126             EditorApplication.delayCall -= ScrollIfNeeded;
00127             
00128             if (selectedItem == null)
00129                 return;
00130 
00131             var minY = List.worldBound.yMin;
00132             var maxY = List.worldBound.yMax;
00133             var itemMinY = selectedItem.worldBound.yMin;
00134             var itemMaxY = selectedItem.worldBound.yMax;
00135             var scroll = List.scrollOffset;
00136 
00137             if (itemMinY < minY)
00138             {
00139                 scroll.y -= minY - itemMinY;
00140                 if (scroll.y <= minY)
00141                     scroll.y = 0;
00142                 List.scrollOffset = scroll;
00143             }
00144             else if (itemMaxY > maxY)
00145             {
00146                 scroll.y += itemMaxY - maxY;
00147                 List.scrollOffset = scroll;
00148             }
00149         }
