00151         private void OnKeyDownShortcut(KeyDownEvent evt)
00152         {
00153             if (selectedItem == null)
00154                 return;
00155 
00156             if (evt.keyCode == KeyCode.Tab)
00157             {
00158                 OnFocusChange();
00159                 evt.StopPropagation();
00160                 return;
00161             }
00162             
00163             if (evt.keyCode == KeyCode.UpArrow)
00164             {
00165                 if (selectedItem.previousItem != null)
00166                 {
00167                     Select(selectedItem.previousItem);
00168                     ScrollIfNeeded();
00169                 }
00170                 else if (selectedItem.packageGroup.previousGroup != null && selectedItem.packageGroup.previousGroup.visible)
00171                 {
00172                     Select(selectedItem.packageGroup.previousGroup.lastPackage);
00173                     ScrollIfNeeded();
00174                 }
00175                 evt.StopPropagation();
00176                 return;
00177             }
00178 
00179             if (evt.keyCode == KeyCode.DownArrow)
00180             {
00181                 if (selectedItem.nextItem != null)
00182                 {
00183                     Select(selectedItem.nextItem);
00184                     ScrollIfNeeded();
00185                 }
00186                 else if (selectedItem.packageGroup.nextGroup != null && selectedItem.packageGroup.nextGroup.visible)
00187                 {
00188                     Select(selectedItem.packageGroup.nextGroup.firstPackage);
00189                     ScrollIfNeeded();
00190                 }
00191                 evt.StopPropagation();
00192                 return;
00193             }
00194 
00195             if (evt.keyCode == KeyCode.PageUp)
00196             {
00197                 if (selectedItem.packageGroup != null)
00198                 {
00199                     if (selectedItem == selectedItem.packageGroup.lastPackage && selectedItem != selectedItem.packageGroup.firstPackage)
00200                     {
00201                         Select(selectedItem.packageGroup.firstPackage);
00202                         ScrollIfNeeded();
00203                     }
00204                     else if (selectedItem == selectedItem.packageGroup.firstPackage && selectedItem.packageGroup.previousGroup != null && selectedItem.packageGroup.previousGroup.visible)
00205                     {
00206                         Select(selectedItem.packageGroup.previousGroup.lastPackage);
00207                         ScrollIfNeeded();
00208                     }
00209                     else if (selectedItem != selectedItem.packageGroup.lastPackage && selectedItem != selectedItem.packageGroup.firstPackage)
00210                     {
00211                         Select(selectedItem.packageGroup.firstPackage);
00212                         ScrollIfNeeded();
00213                     }
00214                 }
00215                 evt.StopPropagation();
00216                 return;
00217             }
00218 
00219             if (evt.keyCode == KeyCode.PageDown)
00220             {
00221                 if (selectedItem.packageGroup != null)
00222                 {
00223                     if (selectedItem == selectedItem.packageGroup.firstPackage && selectedItem != selectedItem.packageGroup.lastPackage)
00224                     {
00225                         Select(selectedItem.packageGroup.lastPackage);
00226                         ScrollIfNeeded();
00227                     }
00228                     else if (selectedItem == selectedItem.packageGroup.lastPackage && selectedItem.packageGroup.nextGroup != null && selectedItem.packageGroup.nextGroup.visible)
00229                     {
00230                         Select(selectedItem.packageGroup.nextGroup.firstPackage);
00231                         ScrollIfNeeded();
00232                     }
00233                     else if (selectedItem != selectedItem.packageGroup.firstPackage && selectedItem != selectedItem.packageGroup.lastPackage)
00234                     {
00235                         Select(selectedItem.packageGroup.lastPackage);
00236                         ScrollIfNeeded();
00237                     }
00238                 }
00239                 evt.StopPropagation();
00240             }
00241         }
