00259         private void SetPackages(IEnumerable<Package> packages)
00260         {
00261             if (PackageCollection.Instance.Filter == PackageFilter.Modules)
00262             {
00263                 packages = packages.Where(pkg => pkg.IsBuiltIn);
00264             }
00265             else if (PackageCollection.Instance.Filter== PackageFilter.All)
00266             {
00267                 packages = packages.Where(pkg => !pkg.IsBuiltIn);
00268             }
00269             else
00270             {
00271                 packages = packages.Where(pkg => !pkg.IsBuiltIn);
00272                 packages = packages.Where(pkg => pkg.Current != null);
00273             }
00274 
00275             OnLoaded();
00276             ClearAll();
00277 
00278             var packagesGroup = new PackageGroup(PackageGroupOrigins.Packages.ToString());
00279             Groups.Add(packagesGroup);
00280             List.Add(packagesGroup);
00281             packagesGroup.previousGroup = null;
00282 
00283             var builtInGroup = new PackageGroup(PackageGroupOrigins.BuiltInPackages.ToString());
00284             Groups.Add(builtInGroup);
00285             List.Add(builtInGroup);
00286 
00287             if ((PackageCollection.Instance.Filter & PackageFilter.Modules) == PackageFilter.Modules)
00288             {
00289                 packagesGroup.nextGroup = builtInGroup;
00290                 builtInGroup.previousGroup = packagesGroup;
00291                 builtInGroup.nextGroup = null;
00292             }
00293             else
00294             {
00295                 packagesGroup.nextGroup = null;
00296                 UIUtils.SetElementDisplay(builtInGroup, false);
00297             }
00298 
00299             var lastSelection = PackageCollection.Instance.SelectedPackage;
00300             Select(null);
00301 
00302             PackageItem defaultSelection = null;
00303 
00304             foreach (var package in packages.OrderBy(pkg => pkg.Versions.FirstOrDefault() == null ? pkg.Name : pkg.Versions.FirstOrDefault().DisplayName))
00305             {
00306                 var item = AddPackage(package);
00307 
00308                 if (null == selectedItem && defaultSelection == null)
00309                     defaultSelection = item;
00310 
00311                 if (null == selectedItem && !string.IsNullOrEmpty(lastSelection) && package.Name.Equals(lastSelection))
00312                     Select(item);
00313             }
00314 
00315             if (selectedItem == null)
00316                 Select(defaultSelection);
00317 
00318             PackageFiltering.FilterPackageList(this);
00319         }
