00321         public void SelectLastSelection()
00322         {
00323             var lastSelection = PackageCollection.Instance.SelectedPackage;
00324             if (lastSelection == null)
00325                 return;
00326             
00327             var list = List.Query<PackageItem>().ToList();
00328             PackageItem defaultSelection = null;
00329 
00330             foreach (var item in list)
00331             {
00332                 if (defaultSelection == null)
00333                     defaultSelection = item;
00334 
00335                 if (!string.IsNullOrEmpty(lastSelection) && item.package.Name.Equals(lastSelection))
00336                 {
00337                     defaultSelection = item;
00338                     break;
00339                 }
00340             }
00341 
00342             selectedItem = null;
00343             Select(defaultSelection);
00344         }
