00346         private PackageItem AddPackage(Package package)
00347         {
00348             var groupName = package.Latest != null ? package.Latest.Group : package.Current.Group;
00349             var group = GetOrCreateGroup(groupName);
00350             var packageItem = group.AddPackage(package);
00351 
00352             packageItem.OnSelected += Select;
00353 
00354             return packageItem;
00355         }
