00357         private PackageGroup GetOrCreateGroup(string groupName)
00358         {
00359             foreach (var g in Groups)
00360             {
00361                 if (g.name == groupName)
00362                     return g;
00363             }
00364 
00365             var group = new PackageGroup(groupName);
00366             var latestGroup = Groups.LastOrDefault();
00367             Groups.Add(group);
00368             List.Add(group);
00369 
00370             group.previousGroup = null;
00371             if (latestGroup != null)
00372             {
00373                 latestGroup.nextGroup = group;
00374                 group.previousGroup = latestGroup;
00375                 group.nextGroup = null;
00376             }
00377             return group;
00378         }
