00380         private void Select(PackageItem packageItem)
00381         {
00382             if (packageItem == selectedItem)
00383                 return;
00384 
00385             var selectedPackageName = packageItem != null ? packageItem.package.Name : null;
00386             PackageCollection.Instance.SelectedPackage = selectedPackageName;
00387 
00388             if (selectedItem != null)
00389                 selectedItem.SetSelected(false); // Clear Previous selection
00390 
00391             selectedItem = packageItem;
00392             if (selectedItem == null)
00393             {
00394                 OnSelected(null);
00395                 return;
00396             }
00397 
00398             selectedItem.SetSelected(true);
00399             ScrollIfNeeded();
00400             OnSelected(selectedItem.package);
00401         }
