00001 using System.Linq;
00002 
00003 namespace UnityEditor.PackageManager.UI
00004 {
00005     internal static class PackageManagerPrefs
00006     {
00007         private const string kShowPreviewPackagesPrefKeyPrefix = "PackageManager.ShowPreviewPackages_";
00008         private const string kShowPreviewPackagesWarningPrefKey = "PackageManager.ShowPreviewPackagesWarning";
00009 
00010         private static string GetProjectIdentifier()
00011         {
00012             // PlayerSettings.productGUID is already used as LocalProjectID by Analytics, so we use it too
00013             return PlayerSettings.productGUID.ToString();
00014         }
00015 
00016         public static bool ShowPreviewPackages
00017         {
00018             get
00019             {
00020                 var key = kShowPreviewPackagesPrefKeyPrefix + GetProjectIdentifier();
00021 
00022                 // If user manually choose to show or not preview packages, use this value
00023                 if (EditorPrefs.HasKey(key))
00024                     return EditorPrefs.GetBool(key);
00025 
00026                 // Returns true if at least one preview package is installed, false otherwise
00027                 return PackageCollection.Instance.LatestListPackages.Any(p => p.IsPreview && p.IsCurrent);
00028             }
00029             set
00030             {
00031                 EditorPrefs.SetBool(kShowPreviewPackagesPrefKeyPrefix + GetProjectIdentifier(), value);
00032             }
00033         }
00034 
00035         public static bool ShowPreviewPackagesWarning
00036         {
00037             get { return EditorPrefs.GetBool(kShowPreviewPackagesWarningPrefKey, true); }
00038             set { EditorPrefs.SetBool(kShowPreviewPackagesWarningPrefKey, value); }
00039         }
00040     }
00041 }
