00132         void ImportCallback(string packageName)
00133         {
00134             if (packageName == "TMP Essential Resources")
00135             {
00136                 k_EssentialResourcesImported = true;
00137                 TMPro_EventManager.ON_RESOURCES_LOADED();
00138 
00139                 #if UNITY_2018_3_OR_NEWER
00140                 SettingsService.NotifySettingsProviderChanged();
00141                 #endif
00142             }
00143             else if (packageName == "TMP Examples & Extras")
00144             {
00145                 k_ExamplesAndExtrasResourcesImported = true;
00146                 k_IsImportingExamples = false;
00147             }
00148 
00149             Debug.Log("[" + packageName + "] have been imported.");
00150 
00151             AssetDatabase.importPackageCompleted -= ImportCallback;
00152         }
