00155         string GetPackageFullPath()
00156         {
00157             // Check for potential UPM package
00158             string packagePath = Path.GetFullPath("Packages/com.unity.textmeshpro");
00159             if (Directory.Exists(packagePath))
00160             {
00161                 return packagePath;
00162             }
00163 
00164             packagePath = Path.GetFullPath("Assets/..");
00165             if (Directory.Exists(packagePath))
00166             {
00167                 // Search default location for development package
00168                 if (Directory.Exists(packagePath + "/Assets/Packages/com.unity.TextMeshPro/Editor Resources"))
00169                 {
00170                     return packagePath + "/Assets/Packages/com.unity.TextMeshPro";
00171                 }
00172 
00173                 // Search for default location of normal TextMesh Pro AssetStore package
00174                 if (Directory.Exists(packagePath + "/Assets/TextMesh Pro/Editor Resources"))
00175                 {
00176                     return packagePath + "/Assets/TextMesh Pro";
00177                 }
00178 
00179                 // Search for potential alternative locations in the user project
00180                 string[] matchingPaths = Directory.GetDirectories(packagePath, "TextMesh Pro", SearchOption.AllDirectories);
00181                 string path = ValidateLocation(matchingPaths, packagePath);
00182                 if (path != null) return packagePath + path;
00183             }
00184 
00185             return null;
00186         }
00187 
00188         string ValidateLocation(string[] paths, string projectPath)
00189         {
00190             for (int i = 0; i < paths.Length; i++)
00191             {
00192                 // Check if the Editor Resources folder exists.
00193                 if (Directory.Exists(paths[i] + "/Editor Resources"))
00194                 {
00195                     string folderPath = paths[i].Replace(projectPath, "");
00196                     folderPath = folderPath.TrimStart('\\', '/');
00197                     return folderPath;
00198                 }
00199             }
00200 
00201             return null;
00202         }
