00017     internal class PackageSearchToolbar : VisualElement
00018     {
00019 #if UNITY_2018_3_OR_NEWER
00020         internal new class UxmlFactory : UxmlFactory<PackageSearchToolbar> { }
00021 #endif
00022         private const string kPlaceHolder = "Search by package name, verified, preview or version number...";
00023         
00024         public event Action OnFocusChange = delegate { };
00025         public event Action<string> OnSearchChange = delegate { };
00026         
00027         private string searchText;
00028         private bool showingPlaceHolder;
00029 
00030         private readonly VisualElement root;
00031 
00032         public PackageSearchToolbar()
00033         {
00034             root = Resources.GetTemplate("PackageSearchToolbar.uxml");
00035             Add(root);
00036             root.StretchToParentSize();
00037 
00038             SearchTextField.value = searchText;
00039             SearchTextField.maxLength = 54;
00040             SearchCancelButton.clickable.clicked += SearchCancelButtonClick;
00041             
00042             RegisterCallback<AttachToPanelEvent>(OnEnterPanel);
00043             RegisterCallback<DetachFromPanelEvent>(OnLeavePanel);
00044 
00045             searchText = PackageSearchFilter.Instance.SearchText;
00046             
00047             if (string.IsNullOrEmpty(searchText))
00048             {
00049                 showingPlaceHolder = true;
00050                 SearchTextField.value = kPlaceHolder;
00051                 SearchTextField.AddToClassList("placeholder");
00052             }
00053             else
00054             {
00055                 showingPlaceHolder = false;
00056                 SearchTextField.value = searchText;
00057                 SearchTextField.RemoveFromClassList("placeholder");
00058             }
00059         }
00060         
00061         public void GrabFocus()
00062         {
00063             SearchTextField.Focus();
00064         }
00065 
00066         public new void SetEnabled(bool enable)
00067         {
00068             base.SetEnabled(enable);
00069             SearchTextField.SetEnabled(enable);
00070             SearchCancelButton.SetEnabled(enable);
00071         }
00072 
00073         private void OnSearchTextFieldChange(ChangeEvent<string> evt)
00074         {
00075             if (showingPlaceHolder && evt.newValue == kPlaceHolder)
00076                 return;
00077             if (!string.IsNullOrEmpty(evt.newValue))
00078                 SearchCancelButton.AddToClassList("on");
00079             else
00080                 SearchCancelButton.RemoveFromClassList("on");
00081 
00082             searchText = evt.newValue;
00083             OnSearchChange(searchText);
00084         }
00085 
00086         private void OnSearchTextFieldFocus(FocusEvent evt)
00087         {
00088             if (showingPlaceHolder)
00089             {
00090                 SearchTextField.value = string.Empty;
00091                 SearchTextField.RemoveFromClassList("placeholder");
00092                 showingPlaceHolder = false;
00093             }
00094         }
00095 
00096         private void OnSearchTextFieldFocusOut(FocusOutEvent evt)
00097         {
00098             if (string.IsNullOrEmpty(searchText))
00099             {
00100                 showingPlaceHolder = true;
00101                 SearchTextField.AddToClassList("placeholder");
00102                 SearchTextField.value = kPlaceHolder;
00103             }
00104         }
00105 
00106         private void SearchCancelButtonClick()
00107         {
00108             if (!string.IsNullOrEmpty(SearchTextField.value))
00109             {
00110                 SearchTextField.value = string.Empty;
00111             }
00112             
00113             showingPlaceHolder = true;
00114             SearchTextField.AddToClassList("placeholder");
00115             SearchTextField.value = kPlaceHolder;
00116         }
00117 
00118         private void OnEnterPanel(AttachToPanelEvent evt)
00119         {
00120             SearchTextField.RegisterCallback<FocusEvent>(OnSearchTextFieldFocus);
00121             SearchTextField.RegisterCallback<FocusOutEvent>(OnSearchTextFieldFocusOut);
00122             SearchTextField.RegisterCallback<ChangeEvent<string>>(OnSearchTextFieldChange);
00123             SearchTextField.RegisterCallback<KeyDownEvent>(OnKeyDownShortcut);
00124         }
00125 
00126         private void OnLeavePanel(DetachFromPanelEvent evt)
00127         {
00128             SearchTextField.UnregisterCallback<FocusEvent>(OnSearchTextFieldFocus);
00129             SearchTextField.UnregisterCallback<FocusOutEvent>(OnSearchTextFieldFocusOut);
00130             SearchTextField.UnregisterCallback<ChangeEvent<string>>(OnSearchTextFieldChange);
00131             SearchTextField.UnregisterCallback<KeyDownEvent>(OnKeyDownShortcut);
00132         }
00133 
00134         private void OnKeyDownShortcut(KeyDownEvent evt)
00135         {
00136             if (evt.keyCode == KeyCode.Escape)
00137             {
00138                 SearchCancelButtonClick();
00139                 SearchCancelButton.Focus();
00140                 evt.StopImmediatePropagation();
00141                 return;
00142             }
00143             
00144             if (evt.keyCode == KeyCode.Tab)
00145             {
00146                 OnFocusChange();
00147                 evt.StopImmediatePropagation();
00148             }
00149         }
00150         private TextField SearchTextField { get { return root.Q<TextField>("searchTextField"); } }
00151         private Button SearchCancelButton { get { return root.Q<Button>("searchCancelButton"); } }
00152     }
00153 }
