00313             packages[0].Version = new SemVersion(1);
00314             packages[1].Version = new SemVersion(4);
00315             packages[2].Version = new SemVersion(2);
00316             packages[3].Version = new SemVersion(5);
00317             packages[4].Version = new SemVersion(3);
00318 
00319             var orderPackages = packages.OrderBy(p => p.Version);
00320             
00321             var package = new Package(kPackageTestName, packages);
00322             
00323             Assert.AreEqual(orderPackages, package.Versions);
00324         }
