00010 using Leap.Unity;
00011 using System.Collections;
00012 using System.Collections.Generic;
00013 using UnityEditor;
00014 using UnityEngine;
00015 
00016 namespace Leap.Unity {
00017 
00018   [CustomEditor(typeof(PostProcessProvider), editorForChildClasses: true)]
00019   public class PostProcessProviderEditor : CustomEditorBase<PostProcessProvider> {
00020 
00021     protected override void OnEnable() {
00022       base.OnEnable();
00023 
00024       // Edit-time pose is only relevant for providers that generate hands.
00025       // Post-process Providers are a special case and don't generate their own hands.
00026       specifyConditionalDrawing(() => false, "editTimePose");
00027 
00028       specifyCustomDecorator("_inputLeapProvider", decorateInputLeapProvider);
00029     }
00030 
00031     private void decorateInputLeapProvider(SerializedProperty property) {
00032       var provider = this.target;
00033 
00034       if (!provider.enabled) {
00035         EditorGUILayout.HelpBox(
00036           message: "This post-process provider is disabled, so it will not output any "
00037                  + "hand data. Use pass-through mode if you only want to disable its "
00038                  + "post-processing and still output hands.",
00039           type: MessageType.Info
00040         );
00041       }
00042       else if (provider.passthroughOnly) {
00043         EditorGUILayout.HelpBox(
00044           message: "This post-process provider is set to pass-through only, so it will "
00045                  + "pass its input unmodified to its output.",
00046           type: MessageType.Info
00047         );
00048       }
00049     }
00050 
00051   }
00052 
00053 }
