00045         protected void InitializeHand()
00046         {
00047             if (handPrefab != null)
00048             {
00049                 handInstance = GameObject.Instantiate(handPrefab);
00050                 handInstance.transform.parent = this.transform;
00051                 handInstance.transform.localPosition = Vector3.zero;
00052                 handInstance.transform.localRotation = Quaternion.identity;
00053                 handInstance.transform.localScale = handPrefab.transform.localScale;
00054                 handSkeleton = handInstance.GetComponent<SteamVR_Behaviour_Skeleton>();
00055                 handSkeleton.origin = Player.instance.trackingOriginTransform;
00056                 handSkeleton.updatePose = false;
00057                 handSkeleton.skeletonAction.onActiveChange += OnSkeletonActiveChange;
00058 
00059                 handRenderers = handInstance.GetComponentsInChildren<Renderer>();
00060                 if (displayHandByDefault == false)
00061                     SetHandVisibility(false);
00062 
00063                 handAnimator = handInstance.GetComponentInChildren<Animator>();
00064 
00065                 if (handSkeleton.skeletonAction.activeBinding == false)
00066                 {
00067                     Debug.LogWarning("Skeleton action: " + handSkeleton.skeletonAction.GetPath() + " is not bound. Your controller may not support SteamVR Skeleton Input.");
00068                     DestroyHand();
00069                 }
00070             }
00071         }
