00191         private void OnRenderModelLoaded(SteamVR_RenderModel loadedRenderModel, bool success)
00192         {
00193             if (controllerRenderModel == loadedRenderModel)
00194             {
00195                 controllerRenderers = controllerInstance.GetComponentsInChildren<Renderer>();
00196                 if (displayControllerByDefault == false)
00197                     SetControllerVisibility(false);
00198 
00199                 if (delayedSetMaterial != null)
00200                     SetControllerMaterial(delayedSetMaterial);
00201 
00202                 if (onControllerLoaded != null)
00203                     onControllerLoaded.Invoke();
00204             }
00205         }
