00396         public void StopAnimation()
00397         {
00398             if (handSkeleton != null)
00399             {
00400                 if (handSkeleton.isBlending == false)
00401                     handSkeleton.BlendToSkeleton();
00402 
00403                 if (CheckAnimatorInit())
00404                     handAnimator.SetInteger(handAnimatorStateId, 0);
00405             }
00406         }
