00408         private bool CheckAnimatorInit()
00409         {
00410             if (handAnimatorStateId == -1 && handAnimator != null)
00411             {
00412                 if (handAnimator.gameObject.activeInHierarchy && handAnimator.isInitialized)
00413                 {
00414                     AnimatorControllerParameter[] parameters = handAnimator.parameters;
00415                     for (int parameterIndex = 0; parameterIndex < parameters.Length; parameterIndex++)
00416                     {
00417                         if (string.Equals(parameters[parameterIndex].name, animatorParameterStateName, StringComparison.CurrentCultureIgnoreCase))
00418                             handAnimatorStateId = parameters[parameterIndex].nameHash;
00419                     }
00420                 }
00421             }
00422 
00423             return handAnimatorStateId != -1 && handAnimator != null && handAnimator.isInitialized;
00424         }
