00010 using Leap.Unity.Interaction;
00011 using System.Collections;
00012 using System.Collections.Generic;
00013 using UnityEngine;
00014 
00015 namespace Leap.Unity.Examples {
00016 
00017   [RequireComponent(typeof(Anchor))]
00018   [AddComponentMenu("")]
00019   public class SimpleAnchorFeedback : MonoBehaviour {
00020 
00021     public Transform scaleTarget;
00022 
00023     private Anchor _anchor;
00024 
00025     private Vector3 _initScaleVector;
00026     private float _curScale = 1F;
00027 
00028     void Start() {
00029       _anchor = GetComponent<Anchor>();
00030 
00031       _initScaleVector = scaleTarget.transform.localScale;
00032     }
00033 
00034     void Update() {
00035       float _targetScale = 1F;
00036 
00037       if (_anchor.isPreferred) {
00038         _targetScale = 1.3F;
00039       }
00040 
00041       if (_anchor.hasAnchoredObjects) {
00042         _targetScale = 2.4F;
00043       }
00044 
00045       _curScale = Mathf.Lerp(_curScale, _targetScale, 20F * Time.deltaTime);
00046 
00047       scaleTarget.transform.localScale = _curScale * _initScaleVector;
00048     }
00049 
00050   }
00051 
00052 }
