00010 using Leap.Unity.Interaction;
00011 using System.Collections;
00012 using System.Collections.Generic;
00013 using UnityEngine;
00014 
00015 namespace Leap.Unity.Examples {
00016 
00017   [AddComponentMenu("")]
00018   [RequireComponent(typeof(InteractionBehaviour))]
00019   public class SimpleInteractionEmission : MonoBehaviour {
00020 
00021     public Color nonPrimaryHoverEmission = Color.black;
00022     public Color primaryHoverEmission = Color.white;
00023 
00024     private Material _material;
00025     private int _emissionColorId;
00026 
00027     private InteractionBehaviour _intObj;
00028 
00029     private Color _targetColor;
00030 
00031     void Start() {
00032       var renderer = GetComponentInChildren<Renderer>();
00033       if (renderer != null) {
00034         _material = renderer.material;
00035         _emissionColorId = Shader.PropertyToID("_EmissionColor");
00036       }
00037 
00038       _intObj = GetComponent<InteractionBehaviour>();
00039     }
00040 
00041     void Update() {
00042       _targetColor = nonPrimaryHoverEmission;
00043 
00044       if (_intObj.isPrimaryHovered) {
00045         _targetColor = primaryHoverEmission;
00046       }
00047 
00048       _material.SetColor(_emissionColorId, Color.Lerp(_material.GetColor(_emissionColorId), _targetColor, 20F * Time.deltaTime));
00049     }
00050 
00051   }
00052 
00053 }
