00010 using InteractionEngineUtility;
00011 using System.Collections;
00012 using System.Collections.Generic;
00013 using UnityEngine;
00014 
00015 namespace Leap.Unity.Interaction {
00016 
00023   public class SlidingWindowThrow : IThrowHandler {
00024 
00028     private float _windowLength = 0.05F;
00029 
00033     private float _windowDelay = 0.02F;
00034 
00039     private AnimationCurve _velocityMultiplierCurve = new AnimationCurve(
00040                                                         new Keyframe(0.0F, 1.0F, 0, 0),
00041                                                         new Keyframe(3.0F, 1.5F, 0, 0));
00042 
00043     private struct VelocitySample {
00044       public float time;
00045       public Vector3 position;
00046       public Quaternion rotation;
00047 
00048       public VelocitySample(Vector3 position, Quaternion rotation, float time) {
00049         this.position = position;
00050         this.rotation = rotation;
00051         this.time = Time.fixedTime;
00052       }
00053 
00054       public static VelocitySample Interpolate(VelocitySample a, VelocitySample b, float time) {
00055         float alpha = Mathf.Clamp01(Mathf.InverseLerp(a.time, b.time, time));
00056 
00057         return new VelocitySample(Vector3.Lerp(a.position, b.position, alpha),
00058                                   Quaternion.Slerp(a.rotation, b.rotation, alpha),
00059                                   time);
00060       }
00061     }
00062 
00063     private Queue<VelocitySample> _velocityQueue = new Queue<VelocitySample>(64);
00064 
00068     public void OnHold(InteractionBehaviour intObj,
00069                        ReadonlyList<InteractionController> controllers) {
00070       _velocityQueue.Enqueue(new VelocitySample(intObj.rigidbody.position,
00071                                                 intObj.rigidbody.rotation,
00072                                                 Time.fixedTime));
00073 
00074       while (true) {
00075         VelocitySample oldestVelocity = _velocityQueue.Peek();
00076 
00077         // Dequeue conservatively if the oldest velocity is more than 4 frames later
00078         // than the start of the window.
00079         if (oldestVelocity.time + (Time.fixedDeltaTime * 4) < Time.fixedTime
00080                                                               - _windowLength
00081                                                               - _windowDelay) {
00082           _velocityQueue.Dequeue();
00083         }
00084         else {
00085           break;
00086         }
00087       }
00088     }
00089 
00093     public void OnThrow(InteractionBehaviour intObj, InteractionController throwingController) {
00094       if (_velocityQueue.Count < 2) {
00095         intObj.rigidbody.velocity = Vector3.zero;
00096         intObj.rigidbody.angularVelocity = Vector3.zero;
00097         return;
00098       }
00099 
00100       float windowEnd = Time.fixedTime - _windowDelay;
00101       float windowStart = windowEnd - _windowLength;
00102 
00103       // 0 occurs before 1,
00104       // start occurs before end.
00105       VelocitySample start0, start1;
00106       VelocitySample end0, end1;
00107       VelocitySample s0, s1;
00108 
00109       s0 = s1 = start0 = start1 = end0 = end1 = _velocityQueue.Dequeue();
00110 
00111       while (_velocityQueue.Count != 0) {
00112         s0 = s1;
00113         s1 = _velocityQueue.Dequeue();
00114 
00115         if (s0.time < windowStart && s1.time >= windowStart) {
00116           start0 = s0;
00117           start1 = s1;
00118         }
00119 
00120         if (s0.time < windowEnd && s1.time >= windowEnd) {
00121           end0 = s0;
00122           end1 = s1;
00123 
00124           // We have assigned both start and end and can break out of the loop.
00125           _velocityQueue.Clear();
00126           break;
00127         }
00128       }
00129 
00130       VelocitySample start = VelocitySample.Interpolate(start0, start1, windowStart);
00131       VelocitySample end = VelocitySample.Interpolate(end0, end1, windowEnd);
00132 
00133       Vector3 interpolatedVelocity = PhysicsUtility.ToLinearVelocity(start.position,
00134                                                                      end.position,
00135                                                                      _windowLength);
00136 
00137       intObj.rigidbody.velocity = interpolatedVelocity;
00138       intObj.rigidbody.angularVelocity = PhysicsUtility.ToAngularVelocity(start.rotation,
00139                                                                           end.rotation,
00140                                                                           _windowLength);
00141 
00142       intObj.rigidbody.velocity *= _velocityMultiplierCurve.Evaluate(intObj.rigidbody.velocity.magnitude);
00143     }
00144 
00145   }
00146 
00147 }
