00021     public class Hand : MonoBehaviour
00022     {
00023         // The flags used to determine how an object is attached to the hand.
00024         [Flags]
00025         public enum AttachmentFlags
00026         {
00027             SnapOnAttach = 1 << 0, // The object should snap to the position of the specified attachment point on the hand.
00028             DetachOthers = 1 << 1, // Other objects attached to this hand will be detached.
00029             DetachFromOtherHand = 1 << 2, // This object will be detached from the other hand.
00030             ParentToHand = 1 << 3, // The object will be parented to the hand.
00031             VelocityMovement = 1 << 4, // The object will attempt to move to match the position and rotation of the hand.
00032             TurnOnKinematic = 1 << 5, // The object will not respond to external physics.
00033             TurnOffGravity = 1 << 6, // The object will not respond to external physics.
00034             AllowSidegrade = 1 << 7, // The object is able to switch from a pinch grab to a grip grab. Decreases likelyhood of a good throw but also decreases likelyhood of accidental drop
00035         };
00036 
00037         public const AttachmentFlags defaultAttachmentFlags = AttachmentFlags.ParentToHand |
00038                                                               AttachmentFlags.DetachOthers |
00039                                                               AttachmentFlags.DetachFromOtherHand |
00040                                                               AttachmentFlags.TurnOnKinematic |
00041                                                               AttachmentFlags.SnapOnAttach;
