00162             set
00163             {
00164                 if (_hoveringInteractable != value)
00165                 {
00166                     if (_hoveringInteractable != null)
00167                     {
00168                         if (spewDebugText)
00169                             HandDebugLog("HoverEnd " + _hoveringInteractable.gameObject);
00170                         _hoveringInteractable.SendMessage("OnHandHoverEnd", this, SendMessageOptions.DontRequireReceiver);
00171 
00172                         //Note: The _hoveringInteractable can change after sending the OnHandHoverEnd message so we need to check it again before broadcasting this message
00173                         if (_hoveringInteractable != null)
00174                         {
00175                             this.BroadcastMessage("OnParentHandHoverEnd", _hoveringInteractable, SendMessageOptions.DontRequireReceiver); // let objects attached to the hand know that a hover has ended
00176                         }
00177                     }
00178 
00179                     _hoveringInteractable = value;
00180 
00181                     if (_hoveringInteractable != null)
00182                     {
00183                         if (spewDebugText)
00184                             HandDebugLog("HoverBegin " + _hoveringInteractable.gameObject);
00185                         _hoveringInteractable.SendMessage("OnHandHoverBegin", this, SendMessageOptions.DontRequireReceiver);
00186 
00187                         //Note: The _hoveringInteractable can change after sending the OnHandHoverBegin message so we need to check it again before broadcasting this message
00188                         if (_hoveringInteractable != null)
00189                         {
00190                             this.BroadcastMessage("OnParentHandHoverBegin", _hoveringInteractable, SendMessageOptions.DontRequireReceiver); // let objects attached to the hand know that a hover has begun
00191                         }
00192                     }
00193                 }
00194             }
00195         }
