00340         //-------------------------------------------------
00341         // Attach a GameObject to this GameObject
00342         //
00343         // objectToAttach - The GameObject to attach
00344         // flags - The flags to use for attaching the object
00345         // attachmentPoint - Name of the GameObject in the hierarchy of this Hand which should act as the attachment point for this GameObject
00346         //-------------------------------------------------
00347         public void AttachObject(GameObject objectToAttach, GrabTypes grabbedWithType, AttachmentFlags flags = defaultAttachmentFlags, Transform attachmentOffset = null)
00348         {
00349             AttachedObject attachedObject = new AttachedObject();
00350             attachedObject.attachmentFlags = flags;
00351             attachedObject.attachedOffsetTransform = attachmentOffset;
00352             attachedObject.attachTime = Time.time;
00353 
00354             if (flags == 0)
00355             {
00356                 flags = defaultAttachmentFlags;
00357             }
00358 
00359             //Make sure top object on stack is non-null
00360             CleanUpAttachedObjectStack();
00361 
00362             //Detach the object if it is already attached so that it can get re-attached at the top of the stack
00363             if(ObjectIsAttached(objectToAttach))
00364                 DetachObject(objectToAttach);
00365 
00366             //Detach from the other hand if requested
00367             if (attachedObject.HasAttachFlag(AttachmentFlags.DetachFromOtherHand))
00368             {
00369                 if (otherHand != null)
00370                     otherHand.DetachObject(objectToAttach);
00371             }
00372 
00373             if (attachedObject.HasAttachFlag(AttachmentFlags.DetachOthers))
00374             {
00375                 //Detach all the objects from the stack
00376                 while (attachedObjects.Count > 0)
00377                 {
00378                     DetachObject(attachedObjects[0].attachedObject);
00379                 }
00380             }
00381 
00382             if (currentAttachedObject)
00383             {
00384                 currentAttachedObject.SendMessage("OnHandFocusLost", this, SendMessageOptions.DontRequireReceiver);
00385             }
00386 
00387             attachedObject.attachedObject = objectToAttach;
00388             attachedObject.interactable = objectToAttach.GetComponent<Interactable>();
00389             attachedObject.handAttachmentPointTransform = this.transform;
00390 
00391             if (attachedObject.interactable != null)
00392             {
00393                 if (attachedObject.interactable.attachEaseIn)
00394                 {
00395                     attachedObject.easeSourcePosition = attachedObject.attachedObject.transform.position;
00396                     attachedObject.easeSourceRotation = attachedObject.attachedObject.transform.rotation;
00397                     attachedObject.interactable.snapAttachEaseInCompleted = false;  
00398                 }
00399 
00400                 if (attachedObject.interactable.useHandObjectAttachmentPoint)
00401                     attachedObject.handAttachmentPointTransform = objectAttachmentPoint;
00402 
00403                 if (attachedObject.interactable.hideHandOnAttach)
00404                     Hide();
00405 
00406                 if (attachedObject.interactable.hideSkeletonOnAttach && mainRenderModel != null && mainRenderModel.displayHandByDefault)
00407                     HideSkeleton();
00408 
00409                 if (attachedObject.interactable.hideControllerOnAttach && mainRenderModel != null && mainRenderModel.displayControllerByDefault)
00410                     HideController();
00411 
00412                 if (attachedObject.interactable.handAnimationOnPickup != 0)
00413                     SetAnimationState(attachedObject.interactable.handAnimationOnPickup);
00414 
00415                 if (attachedObject.interactable.setRangeOfMotionOnPickup != SkeletalMotionRangeChange.None)
00416                     SetTemporarySkeletonRangeOfMotion(attachedObject.interactable.setRangeOfMotionOnPickup);
00417 
00418             }
00419 
00420             attachedObject.originalParent = objectToAttach.transform.parent != null ? objectToAttach.transform.parent.gameObject : null;
00421 
00422             attachedObject.attachedRigidbody = objectToAttach.GetComponent<Rigidbody>();
00423             if (attachedObject.attachedRigidbody != null)
00424             {
00425                 if (attachedObject.interactable.attachedToHand != null) //already attached to another hand
00426                 {
00427                     //if it was attached to another hand, get the flags from that hand
00428                     
00429                     for (int attachedIndex = 0; attachedIndex < attachedObject.interactable.attachedToHand.attachedObjects.Count; attachedIndex++)
00430                     {
00431                         AttachedObject attachedObjectInList = attachedObject.interactable.attachedToHand.attachedObjects[attachedIndex];
00432                         if (attachedObjectInList.interactable == attachedObject.interactable)
00433                         {
00434                             attachedObject.attachedRigidbodyWasKinematic = attachedObjectInList.attachedRigidbodyWasKinematic;
00435                             attachedObject.attachedRigidbodyUsedGravity = attachedObjectInList.attachedRigidbodyUsedGravity;
00436                             attachedObject.originalParent = attachedObjectInList.originalParent;
00437                         }
00438                     }
00439                 }
00440                 else
00441                 {
00442                     attachedObject.attachedRigidbodyWasKinematic = attachedObject.attachedRigidbody.isKinematic;
00443                     attachedObject.attachedRigidbodyUsedGravity = attachedObject.attachedRigidbody.useGravity;
00444                 }
00445             }
00446 
00447             attachedObject.grabbedWithType = grabbedWithType;
00448 
00449             if (attachedObject.HasAttachFlag(AttachmentFlags.ParentToHand))
00450             {
00451                 //Parent the object to the hand
00452                 objectToAttach.transform.parent = this.transform;
00453                 attachedObject.isParentedToHand = true;
00454             }
00455             else
00456             {
00457                 attachedObject.isParentedToHand = false;
00458             }
00459 
00460             if (attachedObject.HasAttachFlag(AttachmentFlags.SnapOnAttach))
00461             {
00462                 if (attachedObject.interactable != null && attachedObject.interactable.skeletonPoser != null && HasSkeleton())
00463                 {
00464                     SteamVR_Skeleton_PoseSnapshot pose = attachedObject.interactable.skeletonPoser.GetBlendedPose(skeleton);
00465 
00466                     //snap the object to the center of the attach point
00467                     objectToAttach.transform.position = this.transform.TransformPoint(pose.position);
00468                     objectToAttach.transform.rotation = this.transform.rotation * pose.rotation;
00469 
00470                     attachedObject.initialPositionalOffset = attachedObject.handAttachmentPointTransform.InverseTransformPoint(objectToAttach.transform.position);
00471                     attachedObject.initialRotationalOffset = Quaternion.Inverse(attachedObject.handAttachmentPointTransform.rotation) * objectToAttach.transform.rotation;
00472                 }
00473                 else
00474                 { 
00475                     if (attachmentOffset != null)
00476                     {
00477                         //offset the object from the hand by the positional and rotational difference between the offset transform and the attached object
00478                         Quaternion rotDiff = Quaternion.Inverse(attachmentOffset.transform.rotation) * objectToAttach.transform.rotation;
00479                         objectToAttach.transform.rotation = attachedObject.handAttachmentPointTransform.rotation * rotDiff;
00480 
00481                         Vector3 posDiff = objectToAttach.transform.position - attachmentOffset.transform.position;
00482                         objectToAttach.transform.position = attachedObject.handAttachmentPointTransform.position + posDiff;
00483                     }
00484                     else
00485                     {
00486                         //snap the object to the center of the attach point
00487                         objectToAttach.transform.rotation = attachedObject.handAttachmentPointTransform.rotation;
00488                         objectToAttach.transform.position = attachedObject.handAttachmentPointTransform.position;
00489                     }
00490 
00491                     Transform followPoint = objectToAttach.transform;
00492 
00493                     attachedObject.initialPositionalOffset = attachedObject.handAttachmentPointTransform.InverseTransformPoint(followPoint.position);
00494                     attachedObject.initialRotationalOffset = Quaternion.Inverse(attachedObject.handAttachmentPointTransform.rotation) * followPoint.rotation;
00495                 }
00496             }
00497             else
00498             {
00499                 if (attachedObject.interactable != null && attachedObject.interactable.skeletonPoser != null && HasSkeleton())
00500                 {
00501                     attachedObject.initialPositionalOffset = attachedObject.handAttachmentPointTransform.InverseTransformPoint(objectToAttach.transform.position);
00502                     attachedObject.initialRotationalOffset = Quaternion.Inverse(attachedObject.handAttachmentPointTransform.rotation) * objectToAttach.transform.rotation;
00503                 }
00504                 else
00505                 {
00506                     if (attachmentOffset != null)
00507                     {
00508                         //get the initial positional and rotational offsets between the hand and the offset transform
00509                         Quaternion rotDiff = Quaternion.Inverse(attachmentOffset.transform.rotation) * objectToAttach.transform.rotation;
00510                         Quaternion targetRotation = attachedObject.handAttachmentPointTransform.rotation * rotDiff;
00511                         Quaternion rotationPositionBy = targetRotation * Quaternion.Inverse(objectToAttach.transform.rotation);
00512 
00513                         Vector3 posDiff = (rotationPositionBy * objectToAttach.transform.position) - (rotationPositionBy * attachmentOffset.transform.position);
00514 
00515                         attachedObject.initialPositionalOffset = attachedObject.handAttachmentPointTransform.InverseTransformPoint(attachedObject.handAttachmentPointTransform.position + posDiff);
00516                         attachedObject.initialRotationalOffset = Quaternion.Inverse(attachedObject.handAttachmentPointTransform.rotation) * (attachedObject.handAttachmentPointTransform.rotation * rotDiff);
00517                     }
00518                     else
00519                     {
00520                         attachedObject.initialPositionalOffset = attachedObject.handAttachmentPointTransform.InverseTransformPoint(objectToAttach.transform.position);
00521                         attachedObject.initialRotationalOffset = Quaternion.Inverse(attachedObject.handAttachmentPointTransform.rotation) * objectToAttach.transform.rotation;
00522                     }
00523                 }
00524             }
00525 
00526 
00527 
00528             if (attachedObject.HasAttachFlag(AttachmentFlags.TurnOnKinematic))
00529             {
00530                 if (attachedObject.attachedRigidbody != null)
00531                 {
00532                     attachedObject.collisionDetectionMode = attachedObject.attachedRigidbody.collisionDetectionMode;
00533                     if (attachedObject.collisionDetectionMode == CollisionDetectionMode.Continuous)
00534                         attachedObject.attachedRigidbody.collisionDetectionMode = CollisionDetectionMode.Discrete;
00535 
00536                     attachedObject.attachedRigidbody.isKinematic = true;
00537                 }
00538             }
00539 
00540             if (attachedObject.HasAttachFlag(AttachmentFlags.TurnOffGravity))
00541             {
00542                 if (attachedObject.attachedRigidbody != null)
00543                 {
00544                     attachedObject.attachedRigidbody.useGravity = false;
00545                 }
00546             }
00547 
00548             if (attachedObject.interactable != null && attachedObject.interactable.attachEaseIn)
00549             {
00550                 attachedObject.attachedObject.transform.position = attachedObject.easeSourcePosition;
00551                 attachedObject.attachedObject.transform.rotation = attachedObject.easeSourceRotation;
00552             }
00553 
00554             attachedObjects.Add(attachedObject);
00555 
00556             UpdateHovering();
00557 
00558             if (spewDebugText)
00559                 HandDebugLog("AttachObject " + objectToAttach);
00560             objectToAttach.SendMessage("OnAttachedToHand", this, SendMessageOptions.DontRequireReceiver);
00561         }
00562 
00563         public bool ObjectIsAttached(GameObject go)
00564         {
00565             for (int attachedIndex = 0; attachedIndex < attachedObjects.Count; attachedIndex++)
00566             {
00567                 if (attachedObjects[attachedIndex].attachedObject == go)
00568                     return true;
00569             }
00570 
00571             return false;
00572         }
