00579         //-------------------------------------------------
00580         // Detach this GameObject from the attached object stack of this Hand
00581         //
00582         // objectToDetach - The GameObject to detach from this Hand
00583         //-------------------------------------------------
00584         public void DetachObject(GameObject objectToDetach, bool restoreOriginalParent = true)
00585         {
00586             int index = attachedObjects.FindIndex(l => l.attachedObject == objectToDetach);
00587             if (index != -1)
00588             {
00589                 if (spewDebugText)
00590                     HandDebugLog("DetachObject " + objectToDetach);
00591 
00592                 GameObject prevTopObject = currentAttachedObject;
00593 
00594 
00595                 if (attachedObjects[index].interactable != null)
00596                 {
00597                     if (attachedObjects[index].interactable.hideHandOnAttach)
00598                         Show();
00599 
00600                     if (attachedObjects[index].interactable.hideSkeletonOnAttach && mainRenderModel != null && mainRenderModel.displayHandByDefault)
00601                         ShowSkeleton();
00602 
00603                     if (attachedObjects[index].interactable.hideControllerOnAttach && mainRenderModel != null && mainRenderModel.displayControllerByDefault)
00604                         ShowController();
00605 
00606                     if (attachedObjects[index].interactable.handAnimationOnPickup != 0)
00607                         StopAnimation();
00608 
00609                     if (attachedObjects[index].interactable.setRangeOfMotionOnPickup != SkeletalMotionRangeChange.None)
00610                         ResetTemporarySkeletonRangeOfMotion();
00611                 }
00612 
00613                 Transform parentTransform = null;
00614                 if (attachedObjects[index].isParentedToHand)
00615                 {
00616                     if (restoreOriginalParent && (attachedObjects[index].originalParent != null))
00617                     {
00618                         parentTransform = attachedObjects[index].originalParent.transform;
00619                     }
00620 
00621                     if (attachedObjects[index].attachedObject != null)
00622                     {
00623                         attachedObjects[index].attachedObject.transform.parent = parentTransform;
00624                     }
00625                 }
00626 
00627                 if (attachedObjects[index].HasAttachFlag(AttachmentFlags.TurnOnKinematic))
00628                 {
00629                     if (attachedObjects[index].attachedRigidbody != null)
00630                     {
00631                         attachedObjects[index].attachedRigidbody.isKinematic = attachedObjects[index].attachedRigidbodyWasKinematic;
00632                         attachedObjects[index].attachedRigidbody.collisionDetectionMode = attachedObjects[index].collisionDetectionMode;
00633                     }
00634                 }
00635 
00636                 if (attachedObjects[index].HasAttachFlag(AttachmentFlags.TurnOffGravity))
00637                 {
00638                     if (attachedObjects[index].attachedObject != null)
00639                     {
00640                         if (attachedObjects[index].attachedRigidbody != null)
00641                             attachedObjects[index].attachedRigidbody.useGravity = attachedObjects[index].attachedRigidbodyUsedGravity;
00642                     }
00643                 }
00644 
00645                 if (attachedObjects[index].interactable != null && attachedObjects[index].interactable.handFollowTransform && HasSkeleton())
00646                 {
00647                     skeleton.transform.localPosition = Vector3.zero;
00648                     skeleton.transform.localRotation = Quaternion.identity;
00649                 }
00650 
00651                 if (attachedObjects[index].attachedObject != null)
00652                 {
00653                     if (attachedObjects[index].interactable == null || (attachedObjects[index].interactable != null && attachedObjects[index].interactable.isDestroying == false))
00654                         attachedObjects[index].attachedObject.SetActive(true);
00655 
00656                     attachedObjects[index].attachedObject.SendMessage("OnDetachedFromHand", this, SendMessageOptions.DontRequireReceiver);
00657                 }
00658 
00659                 attachedObjects.RemoveAt(index);
00660 
00661                 CleanUpAttachedObjectStack();
00662 
00663                 GameObject newTopObject = currentAttachedObject;
00664 
00665                 hoverLocked = false;
00666 
00667 
00668                 //Give focus to the top most object on the stack if it changed
00669                 if (newTopObject != null && newTopObject != prevTopObject)
00670                 {
00671                     newTopObject.SetActive(true);
00672                     newTopObject.SendMessage("OnHandFocusAcquired", this, SendMessageOptions.DontRequireReceiver);
00673                 }
00674             }
00675 
00676             CleanUpAttachedObjectStack();
00677 
00678             if (mainRenderModel != null)
00679                 mainRenderModel.MatchHandToTransform(mainRenderModel.transform);
00680             if (hoverhighlightRenderModel != null)
00681                 hoverhighlightRenderModel.MatchHandToTransform(hoverhighlightRenderModel.transform);
00682         }
