00685         //-------------------------------------------------
00686         // Get the world velocity of the VR Hand.
00687         //-------------------------------------------------
00688         public Vector3 GetTrackedObjectVelocity(float timeOffset = 0)
00689         {
00690             if (trackedObject == null)
00691             {
00692                 Vector3 velocityTarget, angularTarget;
00693                 GetUpdatedAttachedVelocities(currentAttachedObjectInfo.Value, out velocityTarget, out angularTarget);
00694                 return velocityTarget;
00695             }
00696 
00697                 if (isActive)
00698             {
00699                 if (timeOffset == 0)
00700                     return Player.instance.trackingOriginTransform.TransformVector(trackedObject.GetVelocity());
00701                 else
00702                 {
00703                     Vector3 velocity;
00704                     Vector3 angularVelocity;
00705 
00706                     trackedObject.GetVelocitiesAtTimeOffset(timeOffset, out velocity, out angularVelocity);
00707                     return Player.instance.trackingOriginTransform.TransformVector(velocity);
00708                 }
00709             }
00710 
00711             return Vector3.zero;
00712         }
