00796         //-------------------------------------------------
00797         protected virtual IEnumerator Start()
00798         {
00799             // save off player instance
00800             playerInstance = Player.instance;
00801             if (!playerInstance)
00802             {
00803                 Debug.LogError("<b>[SteamVR Interaction]</b> No player instance found in Hand Start()");
00804             }
00805 
00806             // allocate array for colliders
00807             overlappingColliders = new Collider[ColliderArraySize];
00808 
00809             // We are a "no SteamVR fallback hand" if we have this camera set
00810             // we'll use the right mouse to look around and left mouse to interact
00811             // - don't need to find the device
00812             if (noSteamVRFallbackCamera)
00813             {
00814                 yield break;
00815             }
00816 
00817             //Debug.Log( "<b>[SteamVR Interaction]</b> Hand - initializing connection routine" );
00818 
00819             while (true)
00820             {
00821                 if (isPoseValid)
00822                 {
00823                     InitController();
00824                     break;
00825                 }
00826 
00827                 yield return null;
00828             }
00829         }
