00832         //-------------------------------------------------
00833         protected virtual void UpdateHovering()
00834         {
00835             if ((noSteamVRFallbackCamera == null) && (isActive == false))
00836             {
00837                 return;
00838             }
00839 
00840             if (hoverLocked)
00841                 return;
00842 
00843             if (applicationLostFocusObject.activeSelf)
00844                 return;
00845 
00846             float closestDistance = float.MaxValue;
00847             Interactable closestInteractable = null;
00848 
00849             if (useHoverSphere)
00850             {
00851                 float scaledHoverRadius = hoverSphereRadius * Mathf.Abs(SteamVR_Utils.GetLossyScale(hoverSphereTransform));
00852                 CheckHoveringForTransform(hoverSphereTransform.position, scaledHoverRadius, ref closestDistance, ref closestInteractable, Color.green);
00853             }
00854 
00855             if (useControllerHoverComponent && mainRenderModel != null && mainRenderModel.IsControllerVisibile())
00856             {
00857                 float scaledHoverRadius = controllerHoverRadius * Mathf.Abs(SteamVR_Utils.GetLossyScale(this.transform));
00858                 CheckHoveringForTransform(mainRenderModel.GetControllerPosition(controllerHoverComponent), scaledHoverRadius / 2f, ref closestDistance, ref closestInteractable, Color.blue);
00859             }
00860 
00861             if (useFingerJointHover && mainRenderModel != null && mainRenderModel.IsHandVisibile())
00862             {
00863                 float scaledHoverRadius = fingerJointHoverRadius * Mathf.Abs(SteamVR_Utils.GetLossyScale(this.transform));
00864                 CheckHoveringForTransform(mainRenderModel.GetBonePosition((int)fingerJointHover), scaledHoverRadius / 2f, ref closestDistance, ref closestInteractable, Color.yellow);
00865             }
00866 
00867             // Hover on this one
00868             hoveringInteractable = closestInteractable;
00869         }
00870 
00871         protected virtual bool CheckHoveringForTransform(Vector3 hoverPosition, float hoverRadius, ref float closestDistance, ref Interactable closestInteractable, Color debugColor)
00872         {
00873             bool foundCloser = false;
00874 
00875             // null out old vals
00876             for (int i = 0; i < overlappingColliders.Length; ++i)
00877             {
00878                 overlappingColliders[i] = null;
00879             }
00880 
00881             int numColliding = Physics.OverlapSphereNonAlloc(hoverPosition, hoverRadius, overlappingColliders, hoverLayerMask.value);
00882 
00883             if (numColliding == ColliderArraySize)
00884                 Debug.LogWarning("<b>[SteamVR Interaction]</b> This hand is overlapping the max number of colliders: " + ColliderArraySize + ". Some collisions may be missed. Increase ColliderArraySize on Hand.cs");
00885 
00886             // DebugVar
00887             int iActualColliderCount = 0;
00888 
00889             // Pick the closest hovering
00890             for (int colliderIndex = 0; colliderIndex < overlappingColliders.Length; colliderIndex++)
00891             {
00892                 Collider collider = overlappingColliders[colliderIndex];
00893 
00894                 if (collider == null)
00895                     continue;
00896 
00897                 Interactable contacting = collider.GetComponentInParent<Interactable>();
00898 
00899                 // Yeah, it's null, skip
00900                 if (contacting == null)
00901                     continue;
00902 
00903                 // Ignore this collider for hovering
00904                 IgnoreHovering ignore = collider.GetComponent<IgnoreHovering>();
00905                 if (ignore != null)
00906                 {
00907                     if (ignore.onlyIgnoreHand == null || ignore.onlyIgnoreHand == this)
00908                     {
00909                         continue;
00910                     }
00911                 }
00912 
00913                 // Can't hover over the object if it's attached
00914                 bool hoveringOverAttached = false;
00915                 for (int attachedIndex = 0; attachedIndex < attachedObjects.Count; attachedIndex++)
00916                 {
00917                     if (attachedObjects[attachedIndex].attachedObject == contacting.gameObject)
00918                     {
00919                         hoveringOverAttached = true;
00920                         break;
00921                     }
00922                 }
00923                 if (hoveringOverAttached)
00924                     continue;
00925 
00926                 // Occupied by another hand, so we can't touch it
00927                 if (otherHand && otherHand.hoveringInteractable == contacting)
00928                     continue;
00929 
00930                 // Best candidate so far...
00931                 float distance = Vector3.Distance(contacting.transform.position, hoverPosition);
00932                 if (distance < closestDistance)
00933                 {
00934                     closestDistance = distance;
00935                     closestInteractable = contacting;
00936                     foundCloser = true;
00937                 }
00938                 iActualColliderCount++;
00939             }
00940 
00941             if (showDebugInteractables && foundCloser)
00942             {
00943                 Debug.DrawLine(hoverPosition, closestInteractable.transform.position, debugColor, .05f, false);
00944             }
00945 
00946             if (iActualColliderCount > 0 && iActualColliderCount != prevOverlappingColliders)
00947             {
00948                 prevOverlappingColliders = iActualColliderCount;
00949 
00950                 if (spewDebugText)
00951                     HandDebugLog("Found " + iActualColliderCount + " overlapping colliders.");
00952             }
00953 
00954             return foundCloser;
00955         }
00956 
00957 
00958         //-------------------------------------------------
00959         protected virtual void UpdateNoSteamVRFallback()
00960         {
00961             if (noSteamVRFallbackCamera)
00962             {
00963                 Ray ray = noSteamVRFallbackCamera.ScreenPointToRay(Input.mousePosition);
00964 
00965                 if (attachedObjects.Count > 0)
00966                 {
00967                     // Holding down the mouse:
00968                     // move around a fixed distance from the camera
00969                     transform.position = ray.origin + noSteamVRFallbackInteractorDistance * ray.direction;
00970                 }
00971                 else
00972                 {
00973                     // Not holding down the mouse:
00974                     // cast out a ray to see what we should mouse over
00975 
00976                     // Don't want to hit the hand and anything underneath it
00977                     // So move it back behind the camera when we do the raycast
00978                     Vector3 oldPosition = transform.position;
00979                     transform.position = noSteamVRFallbackCamera.transform.forward * (-1000.0f);
00980 
00981                     RaycastHit raycastHit;
00982                     if (Physics.Raycast(ray, out raycastHit, noSteamVRFallbackMaxDistanceNoItem))
00983                     {
00984                         transform.position = raycastHit.point;
00985 
00986                         // Remember this distance in case we click and drag the mouse
00987                         noSteamVRFallbackInteractorDistance = Mathf.Min(noSteamVRFallbackMaxDistanceNoItem, raycastHit.distance);
00988                     }
00989                     else if (noSteamVRFallbackInteractorDistance > 0.0f)
00990                     {
00991                         // Move it around at the distance we last had a hit
00992                         transform.position = ray.origin + Mathf.Min(noSteamVRFallbackMaxDistanceNoItem, noSteamVRFallbackInteractorDistance) * ray.direction;
00993                     }
00994                     else
00995                     {
00996                         // Didn't hit, just leave it where it was
00997                         transform.position = oldPosition;
00998                     }
00999                 }
01000             }
01001         }
