01100         protected virtual void HandFollowUpdate()
01101         {
01102             GameObject attachedObject = currentAttachedObject;
01103             if (attachedObject != null)
01104             {
01105                 if (currentAttachedObjectInfo.Value.interactable != null)
01106                 {
01107                     SteamVR_Skeleton_PoseSnapshot pose = null;
01108 
01109                     if (currentAttachedObjectInfo.Value.interactable.skeletonPoser != null && HasSkeleton())
01110                     {
01111                         pose = currentAttachedObjectInfo.Value.interactable.skeletonPoser.GetBlendedPose(skeleton);
01112                     }
01113 
01114                     if (currentAttachedObjectInfo.Value.interactable.handFollowTransform)
01115                     {
01116                         Quaternion targetHandRotation;
01117                         Vector3 targetHandPosition;
01118 
01119                         if (pose == null)
01120                         {
01121                             Quaternion offset = Quaternion.Inverse(this.transform.rotation) * currentAttachedObjectInfo.Value.handAttachmentPointTransform.rotation;
01122                             targetHandRotation = currentAttachedObjectInfo.Value.interactable.transform.rotation * Quaternion.Inverse(offset);
01123 
01124                             Vector3 worldOffset = (this.transform.position - currentAttachedObjectInfo.Value.handAttachmentPointTransform.position);
01125                             Quaternion rotationDiff = mainRenderModel.GetHandRotation() * Quaternion.Inverse(this.transform.rotation);
01126                             Vector3 localOffset = rotationDiff * worldOffset;
01127                             targetHandPosition = currentAttachedObjectInfo.Value.interactable.transform.position + localOffset;
01128                         }
01129                         else
01130                         {
01131                             Transform objectT = currentAttachedObjectInfo.Value.attachedObject.transform;
01132                             Vector3 oldItemPos = objectT.position;
01133                             Quaternion oldItemRot = objectT.transform.rotation;
01134                             objectT.position = TargetItemPosition(currentAttachedObjectInfo.Value);
01135                             objectT.rotation = TargetItemRotation(currentAttachedObjectInfo.Value);
01136                             Vector3 localSkelePos = objectT.InverseTransformPoint(transform.position);
01137                             Quaternion localSkeleRot = Quaternion.Inverse(objectT.rotation) * transform.rotation;
01138                             objectT.position = oldItemPos;
01139                             objectT.rotation = oldItemRot;
01140 
01141                             targetHandPosition = objectT.TransformPoint(localSkelePos);
01142                             targetHandRotation = objectT.rotation * localSkeleRot;
01143                         }
01144 
01145                         if (mainRenderModel != null)
01146                             mainRenderModel.SetHandRotation(targetHandRotation);
01147                         if (hoverhighlightRenderModel != null)
01148                             hoverhighlightRenderModel.SetHandRotation(targetHandRotation);
01149 
01150                         if (mainRenderModel != null)
01151                             mainRenderModel.SetHandPosition(targetHandPosition);
01152                         if (hoverhighlightRenderModel != null)
01153                             hoverhighlightRenderModel.SetHandPosition(targetHandPosition);
01154                     }
01155                 }
01156             }
01157         }
01158 
01159         protected virtual void FixedUpdate()
01160         {
01161             if (currentAttachedObject != null)
01162             {
01163                 AttachedObject attachedInfo = currentAttachedObjectInfo.Value;
01164                 if (attachedInfo.attachedObject != null)
01165                 {
01166                     if (attachedInfo.HasAttachFlag(AttachmentFlags.VelocityMovement))
01167                     {
01168                         if(attachedInfo.interactable.attachEaseIn == false || attachedInfo.interactable.snapAttachEaseInCompleted)
01169                             UpdateAttachedVelocity(attachedInfo);
01170 
01171                         /*if (attachedInfo.interactable.handFollowTransformPosition)
01172                         {
01173                             skeleton.transform.position = TargetSkeletonPosition(attachedInfo);
01174                             skeleton.transform.rotation = attachedInfo.attachedObject.transform.rotation * attachedInfo.skeletonLockRotation;
01175                         }*/
01176                     }else
01177                     {
01178                         if (attachedInfo.HasAttachFlag(AttachmentFlags.ParentToHand))
01179                         {
01180                             attachedInfo.attachedObject.transform.position = TargetItemPosition(attachedInfo);
01181                             attachedInfo.attachedObject.transform.rotation = TargetItemRotation(attachedInfo);
01182                         }
01183                     }
01184 
01185 
01186                     if (attachedInfo.interactable.attachEaseIn)
01187                     {
01188                         float t = Util.RemapNumberClamped(Time.time, attachedInfo.attachTime, attachedInfo.attachTime + attachedInfo.interactable.snapAttachEaseInTime, 0.0f, 1.0f);
01189                         if (t < 1.0f)
01190                         {
01191                             if (attachedInfo.HasAttachFlag(AttachmentFlags.VelocityMovement))
01192                             {
01193                                 attachedInfo.attachedRigidbody.velocity = Vector3.zero;
01194                                 attachedInfo.attachedRigidbody.angularVelocity = Vector3.zero;
01195                             }
01196                             t = attachedInfo.interactable.snapAttachEaseInCurve.Evaluate(t);
01197                             attachedInfo.attachedObject.transform.position = Vector3.Lerp(attachedInfo.easeSourcePosition, TargetItemPosition(attachedInfo), t);
01198                             attachedInfo.attachedObject.transform.rotation = Quaternion.Lerp(attachedInfo.easeSourceRotation, TargetItemRotation(attachedInfo), t);
01199                         }
01200                         else if (!attachedInfo.interactable.snapAttachEaseInCompleted)
01201                         {
01202                             attachedInfo.interactable.gameObject.SendMessage("OnThrowableAttachEaseInCompleted", this, SendMessageOptions.DontRequireReceiver);
01203                             attachedInfo.interactable.snapAttachEaseInCompleted = true;
01204                         }
01205                     }
01206                 }
01207             }
01208         }
