01210         protected const float MaxVelocityChange = 10f;
01211         protected const float VelocityMagic = 6000f;
01212         protected const float AngularVelocityMagic = 50f;
01213         protected const float MaxAngularVelocityChange = 20f;
01214 
01215         protected void UpdateAttachedVelocity(AttachedObject attachedObjectInfo)
01216         {
01217             Vector3 velocityTarget, angularTarget;
01218             bool success = GetUpdatedAttachedVelocities(attachedObjectInfo, out velocityTarget, out angularTarget);
01219             if (success)
01220             {
01221                 float scale = SteamVR_Utils.GetLossyScale(currentAttachedObjectInfo.Value.handAttachmentPointTransform);
01222                 float maxAngularVelocityChange = MaxAngularVelocityChange * scale;
01223                 float maxVelocityChange = MaxVelocityChange * scale;
01224 
01225                 attachedObjectInfo.attachedRigidbody.velocity = Vector3.MoveTowards(attachedObjectInfo.attachedRigidbody.velocity, velocityTarget, maxVelocityChange);
01226                 attachedObjectInfo.attachedRigidbody.angularVelocity = Vector3.MoveTowards(attachedObjectInfo.attachedRigidbody.angularVelocity, angularTarget, maxAngularVelocityChange);
01227             }
01228         }
01229 
01230         protected Vector3 TargetItemPosition(AttachedObject attachedObject)
01231         {
01232             if (attachedObject.interactable != null && attachedObject.interactable.skeletonPoser != null && HasSkeleton())
01233             {
01234                 Vector3 tp = attachedObject.handAttachmentPointTransform.InverseTransformPoint(transform.TransformPoint(attachedObject.interactable.skeletonPoser.GetBlendedPose(skeleton).position));
01235                 //tp.x *= -1;
01236                 return currentAttachedObjectInfo.Value.handAttachmentPointTransform.TransformPoint(tp);
01237             }
01238             else
01239             {
01240                 return currentAttachedObjectInfo.Value.handAttachmentPointTransform.TransformPoint(attachedObject.initialPositionalOffset);
01241             }
01242         }
01243 
01244         protected Quaternion TargetItemRotation(AttachedObject attachedObject)
01245         {
01246             if (attachedObject.interactable != null && attachedObject.interactable.skeletonPoser != null && HasSkeleton())
01247             {
01248                 Quaternion tr = Quaternion.Inverse(attachedObject.handAttachmentPointTransform.rotation) * (transform.rotation * attachedObject.interactable.skeletonPoser.GetBlendedPose(skeleton).rotation);
01249                 return currentAttachedObjectInfo.Value.handAttachmentPointTransform.rotation * tr;
01250             }
01251             else
01252             {
01253                 return currentAttachedObjectInfo.Value.handAttachmentPointTransform.rotation * attachedObject.initialRotationalOffset;
01254             }
01255         }
01256 
01257         protected bool GetUpdatedAttachedVelocities(AttachedObject attachedObjectInfo, out Vector3 velocityTarget, out Vector3 angularTarget)
01258         {
01259             bool realNumbers = false;
01260 
01261 
01262             float velocityMagic = VelocityMagic;
01263             float angularVelocityMagic = AngularVelocityMagic;
01264 
01265             Vector3 targetItemPosition = TargetItemPosition(attachedObjectInfo);
01266             Vector3 positionDelta = (targetItemPosition - attachedObjectInfo.attachedRigidbody.position);
01267             velocityTarget = (positionDelta * velocityMagic * Time.deltaTime);
01268 
01269             if (float.IsNaN(velocityTarget.x) == false && float.IsInfinity(velocityTarget.x) == false)
01270             {
01271                 if (noSteamVRFallbackCamera)
01272                     velocityTarget /= 10; //hacky fix for fallback
01273 
01274                 realNumbers = true;
01275             }
01276             else
01277                 velocityTarget = Vector3.zero;
01278 
01279 
01280             Quaternion targetItemRotation = TargetItemRotation(attachedObjectInfo);
01281             Quaternion rotationDelta = targetItemRotation * Quaternion.Inverse(attachedObjectInfo.attachedObject.transform.rotation);
01282 
01283 
01284             float angle;
01285             Vector3 axis;
01286             rotationDelta.ToAngleAxis(out angle, out axis);
01287 
01288             if (angle > 180)
01289                 angle -= 360;
01290 
01291             if (angle != 0 && float.IsNaN(axis.x) == false && float.IsInfinity(axis.x) == false)
01292             {
01293                 angularTarget = angle * axis * angularVelocityMagic * Time.deltaTime;
01294 
01295                 if (noSteamVRFallbackCamera)
01296                     angularTarget /= 10; //hacky fix for fallback
01297 
01298                 realNumbers &= true;
01299             }
01300             else
01301                 angularTarget = Vector3.zero;
01302 
01303             return realNumbers;
01304         }
01305 
