01307         //-------------------------------------------------
01308         protected virtual void OnInputFocus(bool hasFocus)
01309         {
01310             if (hasFocus)
01311             {
01312                 DetachObject(applicationLostFocusObject, true);
01313                 applicationLostFocusObject.SetActive(false);
01314                 UpdateHovering();
01315                 BroadcastMessage("OnParentHandInputFocusAcquired", SendMessageOptions.DontRequireReceiver);
01316             }
01317             else
01318             {
01319                 applicationLostFocusObject.SetActive(true);
01320                 AttachObject(applicationLostFocusObject, GrabTypes.Scripted, AttachmentFlags.ParentToHand);
01321                 BroadcastMessage("OnParentHandInputFocusLost", SendMessageOptions.DontRequireReceiver);
01322             }
01323         }
