01326         protected virtual void OnDrawGizmos()
01327         {
01328             if (useHoverSphere && hoverSphereTransform != null)
01329             {
01330                 Gizmos.color = Color.green;
01331                 float scaledHoverRadius = hoverSphereRadius * Mathf.Abs(SteamVR_Utils.GetLossyScale(hoverSphereTransform));
01332                 Gizmos.DrawWireSphere(hoverSphereTransform.position, scaledHoverRadius/2);
01333             }
01334 
01335             if (useControllerHoverComponent && mainRenderModel != null && mainRenderModel.IsControllerVisibile())
01336             {
01337                 Gizmos.color = Color.blue;
01338                 float scaledHoverRadius = controllerHoverRadius * Mathf.Abs(SteamVR_Utils.GetLossyScale(this.transform));
01339                 Gizmos.DrawWireSphere(mainRenderModel.GetControllerPosition(controllerHoverComponent), scaledHoverRadius/2);
01340             }
01341 
01342             if (useFingerJointHover && mainRenderModel != null && mainRenderModel.IsHandVisibile())
01343             {
01344                 Gizmos.color = Color.yellow;
01345                 float scaledHoverRadius = fingerJointHoverRadius * Mathf.Abs(SteamVR_Utils.GetLossyScale(this.transform));
01346                 Gizmos.DrawWireSphere(mainRenderModel.GetBonePosition((int)fingerJointHover), scaledHoverRadius/2);
01347             }
01348         }
