01417         public GrabTypes GetGrabStarting(GrabTypes explicitType = GrabTypes.None)
01418         {
01419             if (explicitType != GrabTypes.None)
01420             {
01421                 if (noSteamVRFallbackCamera)
01422                 {
01423                     if (Input.GetMouseButtonDown(0))
01424                         return explicitType;
01425                 }
01426 
01427                 if (explicitType == GrabTypes.Pinch && grabPinchAction.GetStateDown(handType))
01428                     return GrabTypes.Pinch;
01429                 if (explicitType == GrabTypes.Grip && grabGripAction.GetStateDown(handType))
01430                     return GrabTypes.Grip;
01431             }
01432             else
01433             {
01434                 if (noSteamVRFallbackCamera)
01435                 {
01436                     if (Input.GetMouseButtonDown(0))
01437                         return GrabTypes.Grip;
01438                 }
01439 
01440                 if (grabPinchAction.GetStateDown(handType))
01441                     return GrabTypes.Pinch;
01442                 if (grabGripAction.GetStateDown(handType))
01443                     return GrabTypes.Grip;
01444             }
01445 
01446             return GrabTypes.None;
01447         }
01448 
01449         public GrabTypes GetGrabEnding(GrabTypes explicitType = GrabTypes.None)
01450         {
01451             if (explicitType != GrabTypes.None)
01452             {
01453                 if (noSteamVRFallbackCamera)
01454                 {
01455                     if (Input.GetMouseButtonUp(0))
01456                         return explicitType;
01457                 }
01458 
01459                 if (explicitType == GrabTypes.Pinch && grabPinchAction.GetStateUp(handType))
01460                     return GrabTypes.Pinch;
01461                 if (explicitType == GrabTypes.Grip && grabGripAction.GetStateUp(handType))
01462                     return GrabTypes.Grip;
01463             }
01464             else
01465             {
01466                 if (noSteamVRFallbackCamera)
01467                 {
01468                     if (Input.GetMouseButtonUp(0))
01469                         return GrabTypes.Grip;
01470                 }
01471 
01472                 if (grabPinchAction.GetStateUp(handType))
01473                     return GrabTypes.Pinch;
01474                 if (grabGripAction.GetStateUp(handType))
01475                     return GrabTypes.Grip;
01476             }
01477 
01478             return GrabTypes.None;
01479         }
