01481         public bool IsGrabEnding(GameObject attachedObject)
01482         {
01483             for (int attachedObjectIndex = 0; attachedObjectIndex < attachedObjects.Count; attachedObjectIndex++)
01484             {
01485                 if (attachedObjects[attachedObjectIndex].attachedObject == attachedObject)
01486                 {
01487                     return IsGrabbingWithType(attachedObjects[attachedObjectIndex].grabbedWithType) == false;
01488                 }
01489             }
01490 
01491             return false;
01492         }
01493 
01494         public bool IsGrabbingWithType(GrabTypes type)
01495         {
01496             if (noSteamVRFallbackCamera)
01497             {
01498                 if (Input.GetMouseButton(0))
01499                     return true;
01500             }
01501 
01502             switch (type)
01503             {
01504                 case GrabTypes.Pinch:
01505                     return grabPinchAction.GetState(handType);
01506 
01507                 case GrabTypes.Grip:
01508                     return grabGripAction.GetState(handType);
01509 
01510                 default:
01511                     return false;
01512             }
01513         }
01514 
01515         public bool IsGrabbingWithOppositeType(GrabTypes type)
01516         {
01517             if (noSteamVRFallbackCamera)
01518             {
01519                 if (Input.GetMouseButton(0))
01520                     return true;
01521             }
01522 
01523             switch (type)
01524             {
01525                 case GrabTypes.Pinch:
01526                     return grabGripAction.GetState(handType);
01527 
01528                 case GrabTypes.Grip:
01529                     return grabPinchAction.GetState(handType);
01530 
01531                 default:
01532                     return false;
01533             }
01534         }
01535 
01536         public GrabTypes GetBestGrabbingType()
01537         {
01538             return GetBestGrabbingType(GrabTypes.None);
01539         }
01540 
01541         public GrabTypes GetBestGrabbingType(GrabTypes preferred, bool forcePreference = false)
01542         {
01543             if (noSteamVRFallbackCamera)
01544             {
01545                 if (Input.GetMouseButton(0))
01546                     return preferred;
01547             }
01548 
01549             if (preferred == GrabTypes.Pinch)
01550             {
01551                 if (grabPinchAction.GetState(handType))
01552                     return GrabTypes.Pinch;
01553                 else if (forcePreference)
01554                     return GrabTypes.None;
01555             }
01556             if (preferred == GrabTypes.Grip)
01557             {
01558                 if (grabGripAction.GetState(handType))
01559                     return GrabTypes.Grip;
01560                 else if (forcePreference)
01561                     return GrabTypes.None;
01562             }
01563 
01564             if (grabPinchAction.GetState(handType))
01565                 return GrabTypes.Pinch;
01566             if (grabGripAction.GetState(handType))
01567                 return GrabTypes.Grip;
01568 
01569             return GrabTypes.None;
01570         }
