01573         //-------------------------------------------------
01574         private void InitController()
01575         {
01576             if (spewDebugText)
01577                 HandDebugLog("Hand " + name + " connected with type " + handType.ToString());
01578 
01579             bool hadOldRendermodel = mainRenderModel != null;
01580             EVRSkeletalMotionRange oldRM_rom = EVRSkeletalMotionRange.WithController;
01581             if(hadOldRendermodel)
01582                 oldRM_rom = mainRenderModel.GetSkeletonRangeOfMotion;
01583 
01584 
01585             foreach (RenderModel r in renderModels)
01586             {
01587                 if (r != null)
01588                     Destroy(r.gameObject);
01589             }
01590 
01591             renderModels.Clear();
01592 
01593             GameObject renderModelInstance = GameObject.Instantiate(renderModelPrefab);
01594             renderModelInstance.layer = gameObject.layer;
01595             renderModelInstance.tag = gameObject.tag;
01596             renderModelInstance.transform.parent = this.transform;
01597             renderModelInstance.transform.localPosition = Vector3.zero;
01598             renderModelInstance.transform.localRotation = Quaternion.identity;
01599             renderModelInstance.transform.localScale = renderModelPrefab.transform.localScale;
01600 
01601             //TriggerHapticPulse(800);  //pulse on controller init
01602 
01603             int deviceIndex = trackedObject.GetDeviceIndex();
01604 
01605             mainRenderModel = renderModelInstance.GetComponent<RenderModel>();
01606             renderModels.Add(mainRenderModel);
01607 
01608             if (hadOldRendermodel)
01609                 mainRenderModel.SetSkeletonRangeOfMotion(oldRM_rom);
01610 
01611             this.BroadcastMessage("SetInputSource", handType, SendMessageOptions.DontRequireReceiver); // let child objects know we've initialized
01612             this.BroadcastMessage("OnHandInitialized", deviceIndex, SendMessageOptions.DontRequireReceiver); // let child objects know we've initialized
01613         }
01614 
01615         public void SetRenderModel(GameObject prefab)
01616         {
01617             renderModelPrefab = prefab;
01618 
01619             if (mainRenderModel != null && isPoseValid)
01620                 InitController();
01621         }
