00027         // Set this to false to keep from auto-initializing when calling SteamVR.instance.
00028         private static bool _enabled = true;
00029         public static bool enabled
00030         {
00031             get
00032             {
00033                 if (!XRSettings.enabled)
00034                     enabled = false;
00035                 return _enabled;
00036             }
00037             set
00038             {
00039                 _enabled = value;
00040 
00041                 if (_enabled)
00042                 {
00043                     Initialize();
00044                 }
00045                 else
00046                 {
00047                     SafeDispose();
00048                 }
00049             }
00050         }
