00052         private static SteamVR _instance;
00053         public static SteamVR instance
00054         {
00055             get
00056             {
00057 #if UNITY_EDITOR
00058                 if (!Application.isPlaying)
00059                     return null;
00060 #endif
00061                 if (!enabled)
00062                     return null;
00063 
00064                 if (_instance == null)
00065                 {
00066                     _instance = CreateInstance();
00067 
00068                     // If init failed, then auto-disable so scripts don't continue trying to re-initialize things.
00069                     if (_instance == null)
00070                         _enabled = false;
00071                 }
00072 
00073                 return _instance;
00074             }
00075         }
