00087         public static void Initialize(bool forceUnityVRMode = false)
00088         {
00089             if (forceUnityVRMode)
00090             {
00091                 SteamVR_Behaviour.instance.InitializeSteamVR(true);
00092                 return;
00093             }
00094             else
00095             {
00096                 if (_instance == null)
00097                 {
00098                     _instance = CreateInstance();
00099                     if (_instance == null)
00100                         _enabled = false;
00101                 }
00102             }
00103 
00104             if (_enabled)
00105                 SteamVR_Behaviour.Initialize(forceUnityVRMode);
00106         }
