00115         private static void ReportGeneralErrors()
00116         {
00117             string errorLog = "<b>[SteamVR]</b> Initialization failed. ";
00118 
00119             if (XRSettings.enabled == false)
00120                 errorLog += "VR may be disabled in player settings. Go to player settings in the editor and check the 'Virtual Reality Supported' checkbox'. ";
00121             if (XRSettings.supportedDevices != null && XRSettings.supportedDevices.Length > 0)
00122             {
00123                 if (XRSettings.supportedDevices.Contains("OpenVR") == false)
00124                     errorLog += "OpenVR is not in your list of supported virtual reality SDKs. Add it to the list in player settings. ";
00125                 else if (XRSettings.supportedDevices.First().Contains("OpenVR") == false)
00126                     errorLog += "OpenVR is not first in your list of supported virtual reality SDKs. <b>This is okay, but if you have an Oculus device plugged in, and Oculus above OpenVR in this list, it will try and use the Oculus SDK instead of OpenVR.</b> ";
00127             }
00128             else
00129             {
00130                 errorLog += "You have no SDKs in your Player Settings list of supported virtual reality SDKs. Add OpenVR to it. ";
00131             }
00132 
00133             errorLog += "To force OpenVR initialization call SteamVR.Initialize(true). ";
00134 
00135             Debug.LogWarning(errorLog);
00136         }
